INSERT INTO `jos_content` VALUES (1, 'Handwa', 'handwa', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 cup rice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 cup tuvar dal</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> ¼ cup urad dal</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> ¼ cream of wheat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 200 grams zucchini or dudhi</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 100 grams grated coconut</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1-tbsp. cumin seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1-tsp. sesame seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2-tsp. green chili paste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup plain curd (Yogurt)\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt (to taste)</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Grind rice, tuvar dal and urad dal after soaking for 3-4 hours.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix the plain yogurt and cream of wheat to this blended mixture.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add a little water, if needed to make a slightly soft batter.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Keep it covered for 3-4 hours.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Grate zucchini or dudhi.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add it to the batter.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add salt, chili paste, grated coconut.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix well.Take oil in a frying pan.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Heat the oil and add cumin seeds, sesame seeds, curry leaves and add the batter.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Pour this batter in a tray.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Cook in an oven at 350F for 45 minutes.</span></span></li>\r\n</ol>', 1, 1, 0, 1, '2009-12-19 08:54:49', 62, '', '2010-05-27 12:29:39', 62, 0, '0000-00-00 00:00:00', '2009-12-19 08:54:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 10, 0, 15, 'Snax, Breakfast, Side Food', '', 0, 16, 'robots=\nauthor=Foodie');
INSERT INTO `jos_content` VALUES (2, 'Besi Bele Bhat', 'besibelebhat', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong>:<br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Red gram dal (tur) - 1/2 cup</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Rice - 1 1/2 cups</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Brinjals - 2 small</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Onion - 1 no.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Carrot - 1 no.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Drumsticks - 2 nos (optional)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Tamarind - size of a small lime</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Dry red chillies - 6 (you can reduce or increase as per your taste)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Green chillies - 4 nos</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fenugreek seeds - 1 tsp\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Coriander seeds - 1 tbsp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mustard - 2 tsp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cashewnuts - 20 gms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fresh grated coconut - 1 tbsp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Asafoetida - 1/2 tsp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Curry leaves - Few</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Coriander leaves - Few</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Poppy seeds(khus khus) - 1 tsp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Aniseeds(saunf) - 1/2 tsp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cloves - 4 nos</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cardamoms - 4 nos</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cinnamon - Small piece</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Ghee / butter / margarine / oil - for frying</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt - as per taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong>: <br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel the onions and cut into big pieces. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Chop the vegetables into 2.5 cm pieces. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash rice and dhal. Place it in one of the containers and add 2 1/2 cups of water, a pinch of turmeric powder and chopped vegetables and cook it in a pressure cooker . </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat a little oil in a frying pan. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add 3 red chillies, fenugreek, coriander seeds and grated coconut. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fry until fenugreek turns golden brown in color. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Powder the above and keep it aside. Heat some more oil and fry poppy seeds, cloves, cinnamon, cardamom and aniseeds. Powder them and keep it aside. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Soak tamarind in a little water and extract juice. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat little oil, add remaining dry red chillies, mustard, curry leaves and green chillies. When the mustard crackles, add to the rice and mix well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add tamarind juice and salt to taste and mix thoroughly. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat little ghee and fry cashewnuts until golden brown in color.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add to the rice along with powdered ingredients and masala powder. Mix well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove from fire. If you like ghee, add just before serving. </span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 2, '2009-12-19 09:04:59', 62, '', '2010-05-27 12:33:34', 62, 0, '0000-00-00 00:00:00', '2009-12-19 09:04:59', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 5, 0, 4, '', '', 0, 4, 'robots=\nauthor=');
