INSERT INTO `jos_content` VALUES (21, 'Shayari', 'arz-kiya-hai', '', '<p><span style="font-family: book antiqua,palatino;">Saap Ne Piya Bakri Ka Khoon ...<br />.<br />.<br />Waah! Waah!<br />.<br />.<br />Saap Ne Piya Bakri Ka Khoon ...<br />Good Afternoon! Good Afternoon! Good Afternoon!!<br /> \r\n', '\r\n<br /></span></p>\r\n<p><br /><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;">Tere Pyaar Mein Paagal Ho Gaya Peter ...<br />.<br />.<br />Waah! Waah!<br />.<br />.<br />Tere Pyaar Mein Paagal Ho Gaya Peter ...<br />Ab Hero Honda Splendor, 80 km Prati Litre .. !!</span><br /><br /><br />Bahaar Aane Se Pehle Fizaa Aa Gayii ...<br />.<br />.<br />Waah! Waah!<br />.<br />.<br />Bahaar Aane Se Pehle Fizaa Aa Gayii ...<br />Phool Ko Khilne Se Pehle Bakri Kha Gayii .. !!<br /></span></p>\r\n<p><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">Aatma Chhod Gayii Shareer Puraana ...<br />.<br />.<br />Waah! Waah!<br />.<br />.<br />Aatma Chhod Gayii Shareer Puraana ...<br />Didi Tera Devar Deewana .. !<br /></span><br />Yashomati Maiyya Se Bole Nandlala ...<br />.<br />.<br />Waah! Waah!<br />..<br />.<br />Yashomati Maiyya Se Bole Nandlala ...<br />"Maa, Tata Sky Laga Daala To Life Jhingalala ..!!"<br /><br /><span style="font-family: comic sans ms,sans-serif;">Hoton Pe "Haan" Hai ...<br />Dil Mein "Naa" Hain .....<br />.<br />.<br />Waah! Waah!<br />.<br />.<br />Hoton Pe "Haan" Hai ...<br />Dil Mein "Naa" Hain ...<br />Shashi Kapoor Kehta Hai: "Mere Paas Maa Hai ..." <br /><br /></span>Aapki Surat Mere Dil Mein Aise Bass Gayii Hai ...<br />.<br />.<br />Waah! Waah!<br />.<br />.<br />Aapki Surat Mere Dil Mein Aise Bass Gayii Hai ...<br />Jaise Chhote Se Darwaaze Mein Bhens Phass Gayii Hai .. !!</span></p>\r\n<p> </p>', 1, 2, 0, 17, '2009-12-29 17:08:15', 62, '', '2010-04-23 11:33:32', 62, 0, '0000-00-00 00:00:00', '2009-12-29 17:08:15', '0000-00-00 00:00:00'