INSERT INTO `jos_content` VALUES (119, 'Creation', 'creation', '', '<p><span style="font-family: book antiqua,palatino;">A man said to  his wife  one day, "I don''t know how you can be <br />so stupid and so  beautiful   all at the same time. <br />" The wife responded, "Allow me to explain. \r\n', '\r\n<br />God    made me beautiful so you would be attracted to me; <br />God made me   stupid so I  would be attracted to you! <br /></span></p>', 1, 2, 0, 12, '2010-04-23 17:00:54', 62, '', '2010-04-23 17:01:55', 62, 0, '0000-00-00 00:00:00', '2010-04-23 17:00:54', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 9, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (120, 'HE BREWS', 'he-brews', '', '<p><span style="font-family: book antiqua,palatino;">A man and his  wife were  having an argument about who should brew the coffee each  morning.  <br />The  wife said, "You should do it, because you get up first,  and  then we   don''t have to wait as long to get our coffee."  \r\n', '\r\nThe husband said, "   You are  in charge of cooking around here and you should do it, because  that  is your  job, and I can just wait for my coffee." <br />Wife  replies, "No, you  should do  it, and besides, it is in the Bible that  the man should do the  coffee."  <br />Husband replies, "I can''t believe  that, show me." <br />So she fetched  the  Bible, and opened the New  Testament and showed him at the top of  several  pages, that it indeed  says.........."HE BREWS" </span></p>', 1, 2, 0, 12, '2010-04-23 17:02:56', 62, '', '2010-04-23 17:03:40', 62, 0, '0000-00-00 00:00:00', '2010-04-23 17:02:56', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 5, 'robots=\nauthor=');
