INSERT INTO `jos_content` VALUES (32, 'Patriotism 1', 'patriotism-1', '', '<div>\r\n<p>IF YOU WORSHIP YOUR BOLLYWOOD STAR OR YOUR CRICKETING IDOL….. THINK AGAIN…</p>\r\n</div>\r\n<div>\r\n<p>THERE ARE OTHER PEOPLE WHO  DESERVE IT MUCH MORE THAN THEM……. READ THIS MAIL FULLY….  ESPECIALLY THE LETTER WRITTEN BY ONE OF THE JAWAANS TO HIS FAMILY  …. SALUTE THEM FOR ALL THEIR SACRIFICE….</p>\r\n</div>\r\n<div>\r\n<p>READ THE LETTER BELOW…………….. <br /><br />WE RECAPTURED OUR LAST HILL FROM PAKISTAN <br />BUT WE LOST OUR MOST VALUABLE, GREAT WARRIORS, BRAVE BROTHERS .TODAY IT''S TIME TO REMEMBER THEM <br /><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.31&amp;disp=emb&amp;realattid=0.0.1.1&amp;zw" border="0" width="70" height="123" /></p>\r\n</div>\r\n<div>\r\n<p><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.4&amp;disp=emb&amp;realattid=0.0.1.2&amp;zw" border="0" width="465" height="108" /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.31&amp;disp=emb&amp;realattid=0.0.1.1&amp;zw" border="0" width="70" height="123" /></p>\r\n</div>\r\n<div>\r\n<p><br /><br /><br />A SALUTE TO <br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.22&amp;disp=emb&amp;realattid=0.0.1.3&amp;zw" border="0" width="68" height="50" /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.24&amp;disp=emb&amp;realattid=0.0.1.4&amp;zw" border="0" width="237" height="44" /> <img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.22&amp;disp=emb&amp;realattid=0.0.1.3&amp;zw" border="0" width="68" height="50" /></p>\r\n</div>\r\n<div>\r\n<p><br />OF KARGIL WAR  <br /><br />KARGIL''S FIRST HERO <br /><br />Lt. Saurabh Kalia<br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.23&amp;disp=emb&amp;realattid=0.0.1.5&amp;zw" border="0" width="323" height="83" /> <br /><br />His UNIFORM as a MEMORY <br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.3&amp;disp=emb&amp;realattid=0.0.1.6&amp;zw" border="0" width="400" height="204" /></p>\r\n</div>\r\n<div>\r\n<p><br /><br /><br /> <img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.16&amp;disp=emb&amp;realattid=0.0.1.7&amp;zw" border="0" width="136" height="173" /><br /><br />Capt.Vikram Batra <br /> Param Vir Chakra(Posthumous) <br /><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.30&amp;disp=emb&amp;realattid=0.0.1.8&amp;zw" border="0" width="137" height="167" /> <br /><br />Grenedier. Yogendra Singh<br />Param Vir Chakra  <br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.18&amp;disp=emb&amp;realattid=0.0.1.9&amp;zw" border="0" width="142" height="169" /> <br /><br />RFN . Sanjay Kumar<br />(Param Vir Chakra) <br /><br /> <img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.8&amp;disp=emb&amp;realattid=0.0.1.10&amp;zw" border="0" width="133" height="147" /><br /><br />Major Padmapani Acharya,<br />Maha Vir Chakra (Posthumous)  <br />Of the 2nd Battalion of The RAJPUTANA RIFLES<br /><br /><br /> <img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.21&amp;disp=emb&amp;realattid=0.0.1.11&amp;zw" border="0" width="131" height="147" /></p>\r\n</div>\r\n<div>\r\n<p><br /><br />Lieutenant Balwan Singh, <br />Maha Vir Chakra<br />Of the 18th Battalion of GRENADIERS Regiment <br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.1&amp;disp=emb&amp;realattid=0.0.1.12&amp;zw" border="0" width="118" height="134" /> <br /><br />Major M Saravanan, <br />VirChakra,<br />1 Bihar <br /><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.11&amp;disp=emb&amp;realattid=0.0.1.13&amp;zw" border="0" width="144" height="185" /> <br />Lieutenant Kanad Bhattacharya,<br />Sena Medal (Posthumous)(22 YEARS) <br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.33&amp;disp=emb&amp;realattid=0.0.1.14&amp;zw" border="0" width="140" height="136" /> <br />Captain Saju Cherian, <br />Sena Medal<br />307 Medium Regiment <br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.17&amp;disp=emb&amp;realattid=0.0.1.15&amp;zw" border="0" width="127" height="147" /> <br /><br />Lieutenant Keishing Clifford Nangrum,<br />Maha Vir Chakra  (Posthumous)<br />Of the 12th Battalion of JAMMU AND KASHMIR Light Infantry <br /> <br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.5&amp;disp=emb&amp;realattid=0.0.1.16&amp;zw" border="0" width="133" height="165" /></p>\r\n</div>\r\n<div>\r\n<p><br /><br />Captain R Jerry Prem Raj, <br />Vir Chakra (Posthumous),<br />158 Medium Regiment <br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.13&amp;disp=emb&amp;realattid=0.0.1.17&amp;zw" border="0" width="127" height="158" /> <br /><br />Major Sonam Wangchuk, <br />Maha Vir Chakra<br />Of the LADAKH Scouts <br /><br /><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.27&amp;disp=emb&amp;realattid=0.0.1.18&amp;zw" border="0" width="499" height="309" /></p>\r\n</div>\r\n<div>\r\n<p>v<br /> <br />Officers &amp; Jawans from the 2nd Rajputana Rifles pray before going into battle, to get back Tololing Top. Behind the jawan in the foreground (with a 5.56mm INSAS rifle) is Captain Vijayant Thapar  &lt;http://www.captainvijyantthapar.com/&gt; . His face is partly hidden and is seen sporting a beard. Lieutenant Thapar laid down his life, in the capture of Tololing Top and was awarded the Vir Chakra posthumously for his valour.  <br /><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.2&amp;disp=emb&amp;realattid=0.0.1.19&amp;zw" border="0" width="394" height="297" /></p>\r\n</div>\r\n<p style="text-align: center;" align="center"><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.8&amp;disp=emb&amp;realattid=0.0.1.10&amp;zw" border="0" width="133" height="147" /> <br /><br />Major Padmapani Acharya, 2nd Rajputana Rifles, leads his men into battle after the successful capture of Tololing Top. The 2 Raj. Rifles'' next assignment was to capture the Knoll mountain feature in the Black Rock area, which is in the Drass sub-sector. It was here, that Major Acharya laid down his life in the highest traditions of the Indian Army on 29 June 1999. He was awarded the Maha Vir Chakra, posthumously .  <br /><br /><br /><br />Making the way through narrow valley <br /><br /> <br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.19&amp;disp=emb&amp;realattid=0.0.1.20&amp;zw" border="0" width="409" height="310" /></p>\r\n<p style="text-align: center;" align="center"><br /><br /> <br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.25&amp;disp=emb&amp;realattid=0.0.1.21&amp;zw" border="0" width="447" height="280" /> <br /><br />The Leh-Batalik road is as notorious as the Srinagar-Kargil road. At its beginning lie staging areas, at its end fierce fighting and often, death. Jawans patrol the rugged slopes near Batalik. For them, there is little time for rest and little time to think, always poised on the edge of action. <br /><br /><br />Capt.Vijayant Thapar  (Robin) <br />He Laid down for OUR BETTER TOMORROW  <br />At the age of only 22 <br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.7&amp;disp=emb&amp;realattid=0.0.1.22&amp;zw" border="0" width="465" height="274" /></p>\r\n<p style="text-align: center;" align="center"><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.32&amp;disp=emb&amp;realattid=0.0.1.23&amp;zw" border="0" width="211" height="153" /> <img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.10&amp;disp=emb&amp;realattid=0.0.1.24&amp;zw" border="0" width="88" height="146" /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.26&amp;disp=emb&amp;realattid=0.0.1.25&amp;zw" border="0" width="241" height="150" /></p>\r\n<p style="text-align: center;" align="center">His Last Letter to his parents</p>\r\n<p style="text-align: center;" align="center"><br />Moments before the final assault Capt. Vijyant Thapar (Robin) left this letter at the war front base, to be handed over to his family.</p>\r\n<p style="text-align: center;" align="center"><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.12&amp;disp=emb&amp;realattid=0.0.1.26&amp;zw" border="0" width="582" height="307" /> <br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.15&amp;disp=emb&amp;realattid=0.0.1.27&amp;zw" border="0" width="554" height="1038" /> <br /><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.29&amp;disp=emb&amp;realattid=0.0.1.28&amp;zw" border="0" width="594" height="339" /> <br /><br /><br />AND AFTER THAT</p>\r\n<p style="text-align: center;" align="center">HE CAME BACK HOME WITH TRI COLOR DRAPED <br /><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.14&amp;disp=emb&amp;realattid=0.0.1.29&amp;zw" border="0" width="477" height="303" /></p>\r\n<p style="text-align: center;" align="center"><br /><br /><img src="https://mail.google.com/mail/?ui=2&amp;ik=298564e48b&amp;view=att&amp;th=1141560f2f26e3a9&amp;attid=0.9&amp;disp=emb&amp;realattid=0.0.1.30&amp;zw" border="0" width="476" height="308" /> <br /><br />Jawans from the 2nd Rajputana Rifles, remember their 23 comrades who fell in the decisive battle for the Tololing Top. The battalion earned four Maha Vir Chakras, one of India ''s highest medals for gallantry, three of them being awarded posthumously. <br /><br /><br /> <br /></p>\r\n<p style="text-align: center;" align="center"><br /><br /><br />Captain (Dr.) Rajshree Gupta, Army Medical Corps (AMC), salutes the tricolour-draped coffin of her husband, Major Vivek Gupta of the 2nd Rajputana Rifles, who died fighting the enemies.</p>\r\n<p style="text-align: center;" align="center"><br /><br /></p>\r\n<p style="text-align: center;" align="center"><br /><br /><br /></p>\r\n<p style="text-align: center;" align="center"><br /> JAIHIND!!!!</p>\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td style="padding: 0in; background: none repeat scroll 0% 50% white; width: 100%;" width="100%" bgcolor="white">\r\n<p> </p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style="text-align: center;" align="center">KINDLY SPARE A MAIL TO FORWARD THIS TO YOUR FRIENDS ALONG WITH OTHER IMPORTANT MAILS U SEND THRU…..</p>\r\n<div>\r\n<p>RED SALUTE TO ALL THOSE MARTIERS</p>\r\n</div>\r\n<div style="margin-bottom: 5pt;">\r\n<p> </p>\r\n</div>', '', 0, 0, 0, 0, '2009-12-29 17:33:21', 62, '', '2010-04-23 14:20:48', 62, 0, '0000-00-00 00:00:00', '2009-12-29 17:33:21', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 10, '', '', 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (105, 'Awesome quotes for the genius', 'awesome-quotes-for-the-genius', '', '<div><span style="font-family: book antiqua,palatino;">On The Birthday of the Genius  Sir Sachin Tendulkar... .The Lord of Cricket.<br /></span></div>\r\n<div><span style="font-family: comic sans ms,sans-serif;">@Hashim Amla</span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"Nothing bad can happen to us if  we''re on a plane in India with Sachin Tendulkar on it."<br /><br /></span></div>\r\n<div><span style="font-family: comic sans ms,sans-serif;">@Yaseer Hameed</span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"Sometimes you get so engrossed  in watching batsmen like Rahul Dravid and Sachin Tendulkar that you lose focus on your job."<br /></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"Tuzhe pata hai tune kiska catch  chhoda hai?"</span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"Tuzhe pata hai tune kiska catch  chhoda hai?"<span style="font-family: comic sans ms,sans-serif;"> Wasim Akram to Abdul Razzaq when the latter dropped Sachin''s catch.<br /></span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"To Sachin, the man we all want  to be" What <span style="font-family: comic sans ms,sans-serif;">Andrew Symonds</span> wrote on an aussie t-shirt he autographed specially  for Sachin</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br />Beneath the helmet, under that  unruly curly hair, inside the cranium, there is something we don''t know, something beyond scientific measure. Something  that allows him to soar, to roam a territory of sport that, forget  us, even those who are gifted enough to play alongside him cannot even fathom. When he goes out to bat, people switch on their TV sets and switch  off their lives " <span style="font-family: comic sans ms,sans-serif;">BBC on Sachin</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><br />Brian Charles Lara </span>Sachin is a genius. I''m a mere  mortal.<br /></span></div>\r\n<div><span style="font-family: comic sans ms,sans-serif;">@Mark Taylor</span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"We did not lose to a team  called India...we lost to a man called Sachin" - Mark Taylor, during the test match in Chennai (1997)<br /></span></div>\r\n<div><span style="font-family: comic sans ms,sans-serif;">@M.L.Jaisimha:</span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"The more I see of him the more  confused I''m getting to which is his best knock."</span></div>\r\n<div><span style="font-family: comic sans ms,sans-serif;"><br />@McGrath</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br />"The joy he brings to the  millions of his countrymen, the grace with which he handles all the adulation and the expectations and his innate humility  - all make for a one-in-a-billion individual,"</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">@Wife Anjali</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"I can be hundred per cent sure  that Sachin will not play for a minute longer when he is not enjoying himself. He is still so eager to go out there  and play. He will play as long as he feels he can play,"</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">by HAYDEN</span><br />"I HAVE SEEN GOD, HE BATS AT NO.4 FOR INDIA"<br /><br /><span style="font-family: comic sans ms,sans-serif;">@Daughter</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">"Even my father''s name is Sachin  Tendulkar." Tendulkar''s daughter, Sara,  tells her class her father''s name after the teacher informs them of a restaurant of the same name in Mumbai<br /></span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;">KUMBLE</span> : I am fortunate that  I''ve to bowl at him only in the nets!</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">@Shahrukh Khan</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">Who do you think as most  important celebrity ?<br /> Shahrukh: There was a big party where stars from bollywood and cricket were  invited. Suddenly, there was a big noise, all wanted to see approaching  Amitabh Bachhan. Then Sachin entered the hall and Amitabh was  leading the queue to get a grab of the GENIUS!!</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">@Navjot Singh Sidhu</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">India me aap PrimeMinister ko ek  Baar Katghare me khada kar sakte hain.. Par Sachin Tendulkar par Ungli nahi utha Sakte..</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">@waqar younis</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">He can play that leg glance with  a walking stick also . <br /><br />A banner once said-''<span style="font-family: comic sans ms,sans-serif;"> I WILL SEE  GOD WHEN I DIE BUT TILL THEN I WILL SEE SACHIN </span>'' that quiet defines Sachin- The greatest.<br /></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">Sachin Tendulkar has often  reminded me of a veteran army colonel who has many medals on his chest to show how he has conquered bowlers all over the  world -- <span style="font-family: comic sans ms,sans-serif;">Allan Donald</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br />And i remember reading in one of  Allan Donald''s interview. This interview was in Cricket Talk and 7-8 yrs ago.I was bowling to Sachin and he  hit me for two fours in a row. One from point and the other in between point and gully. That was the last two balls  of the over and the over after that we (SA) took a wicket and during  the group meeting i told Jonty (Rhodes) to be alert and i know a way  to pin Sachin. And i delivered the first ball of my next over and it was a fuller length delevery outside offstump. And i shouted catch. To my  astonishment the ball was hit to the cover boundary. Such was the brilliance  of Sachin. His reflex time is the best i have ever seen. Its like 1/20th of a sec. To get his wicket better not prepare. Atleast u wont  regret if he hits you for boundaries. <span style="font-family: comic sans ms,sans-serif;">Peter Rebouck - aussie  journalist</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br />On a train from Shimla to Delhi,  there was a halt in one of the stations.<br />The train stopped by for few minutes as usual. Sachin  was nearing century, batting on 98. The passengers, railway officials,  everyone on the train waited for Sachin to complete the<br />century. This  Genius can stop time in India!!</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">NKP Salve, former Union Minister</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">This was when he was accused of  ball tempering "Sachin cannot cheat. He is to  cricket what (Mahatma) Gandhiji was to politics. It''s clear discrimination. "</span></div>\r\n<div><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">Andy Flower:</span></span></div>\r\n<div><span style="font-family: book antiqua,palatino;">There are 2 kind of batsmen in  the world. One Sachin Tendulkar. Two all the others.</span></div>', '', 1, 6, 0, 7, '2010-04-23 15:58:45', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-23 15:58:45', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 2, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (106, 'Love Story of Ralph and Edna', 'love-story-of-ralph-and-edna', '', '<p><span style="font-family: book antiqua,palatino;">The love  story of         Ralph and Edna...<br /><br /><br /> Just because someone doesn''t love you the way you want them to,  doesn''t         mean they don''t love you with all they have. Ralph and Edna were  both         patients in a mental hospital. One day while they were walking  past the         hospital swimming pool. Ralph suddenly jumped into the deep end.<br /><br /><br /><br /><br /> He sank to the bottom of the pool and stayed there.<br /><br /><br /><br /><br /> Edna promptly jumped in to save him. She swam to the bottom and  pulled         him out. When the Head Nurse Director became aware of Edna''s  heroic act         she immediately ordered her to be discharged from the hospital,  as she         now considered her to be mentally stable.<br /><br /><br /><br /><br /> When she went to tell Edna the news she said, ''Edna, I have good  news         and bad news. The good news is you''re being discharged, since  you were         able to rationally respond to a crisis by jumping in and saving  the         life of the person you love. I have concluded that your act  displays         sound mindedness.<br /><br /><br /><br /><br /> The bad news is, Ralph hung himself in the bathroom with his  bathrobe         belt right after you saved him. I am so sorry, but he''s dead.''<br /><br /><br /><br /><br /> Edna replied, ''He didn''t hang himself, I put him there to dry.  How soon         can I go home?''<br /><br /><br /><br /><br /> Happy Mental Health Day!<br /><br /><br /><br /><br /> You can do your bit by remembering to send an email to an  unstable         friend...<br /><br /><br /><br /><br /> I have done my part!!! ;););)</span></p>', '', 1, 3, 0, 5, '2010-04-23 16:12:51', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-23 16:12:51', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 5, '', '', 0, 10, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (107, 'Best Poem', 'best-poem', '', '<p><tt><span style="font-size: medium;">This poem was nominated by UN as the  best poem of 2006, Written by an<br />African Kid:</span><br /><br /><span style="font-family: Monotype Corsiva; color: #008000; font-size: medium;"><strong><span style="color: #000000;"><span style="color: #800000;">When I born, I</span> black<br /><span style="color: #808000;">When I grow up,  I</span> black<br /><span style="color: #008000;">When I go in Sun,</span> <span style="color: #008000;">I </span>black<br /><span style="color: #0000ff;">When I scared, I</span> black<br /><span style="color: #ff0000;">When I sick, I</span> black<br /><span style="color: #ff00ff;">And when I die, I</span> still black</span><br /><br /><span style="color: #000000;">And you <span style="color: #0000ff;">white</span> fellow</span><br /><span style="color: #000000;">When you born, you</span><span style="color: #ff00ff;"> pink</span><br /><span style="color: #000000;">When you grow up, you</span> </strong><strong><span style="color: #000000;">white<br />When you go in sun, you</span></strong><strong><span style="color: #ff0000;"> red<br /></span><span style="color: #000000;">When you cold, you</span> <span style="color: #0000ff;">blue</span><br /><span style="color: #000000;">When you  scared, you</span> </strong><strong><span style="color: #ffff00;">yellow<br /></span><span style="color: #000000;">When  you sick, you</span> <span style="color: #00ff00;">green</span></strong><br /><strong><span style="color: #000000;">And when you die, you</span> <span style="color: #808080;">gray</span><br /><br /><span style="color: #800080;">And you calling me  colored??</span></strong></span></tt><span style="font-family: Monotype Corsiva; color: #008000; font-size: medium;"> </span></p>', '', 1, 3, 0, 5, '2010-04-23 16:15:11', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-23 16:15:11', '0000-00-00 00:00:00', '', '', 'show_