INSERT INTO `jos_content` VALUES (68, 'Tips to enjoy work hours', 'tips-to-enjoy-work-hours', '', '<table style="border-collapse: collapse;" border="0" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td style="border: 2.25pt 2.25pt medium solid solid none #cc3300 #cc3300 -moz-use-text-color;" width="702" valign="top">\r\n<div class="MsoNormal"><span style="font-family: Times New Roman; font-size: small;"></span></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: medium 2.25pt none solid -moz-use-text-color #cc3300;" width="702" valign="top">\r\n<div class="MsoNormal" style="text-align: center;"><strong><span style="font-family: Trebuchet MS; color: navy; font-size: small;"><span style="font-weight: bold; font-size: 12pt; color: navy; font-family: ''Trebuchet MS'';">Certain simple techniques to cope with our increasingly demanding work-styles.</span></span></strong><strong><span style="font-family: Trebuchet MS; color: red; font-size: medium;"><span style="font-weight: bold; font-size: 14pt; color: red; font-family: ''Trebuchet MS'';"> </span></span></strong><span style="font-family: Trebuchet MS; color: navy; font-size: xx-small;"><span style="font-size: 9pt; color: navy; font-family: ''Trebuchet MS'';" lang="EN-GB"> </span></span><span style="color: navy;"></span></div>\r\n<div>\r\n<div>\r\n<table style="border-collapse: collapse;" border="0" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td style="border: 1pt solid windowtext;" width="295" valign="top">\r\n<div class="MsoNormal"><strong><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-weight: bold; font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Suggestions</span></span></strong><span style="font-family: Trebuchet MS;"><span style="font-family: ''Trebuchet MS'';"> </span></span></div>\r\n</td>\r\n<td style="border: 1pt 1pt 1pt medium solid solid solid none windowtext windowtext windowtext -moz-use-text-color;" width="295" valign="top">\r\n<div class="MsoNormal"><strong><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-weight: bold; font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Benefits</span></span></strong><span style="font-family: Trebuchet MS;"><span style="font-family: ''Trebuchet MS'';"> </span></span></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: medium 1pt 1pt none solid solid -moz-use-text-color windowtext windowtext;" width="295" valign="top">\r\n<div class="MsoNormal"><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Try not to use the </span></span><strong><span style="font-family: Trebuchet MS; color: red;"><span style="font-weight: bold; color: red; font-family: ''Trebuchet MS'';">intercom</span></span></strong><span style="font-family: Trebuchet MS; color: #333333;"><span style="color: #333333; font-family: ''Trebuchet MS'';">. Rather, walk to your colleagues and discuss matters. </span></span></div>\r\n</td>\r\n<td style="border: medium 1pt 1pt medium none solid solid none -moz-use-text-color windowtext windowtext -moz-use-text-color;" width="295" valign="top">\r\n<div class="MsoNormal"><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Stretches your back, and leg muscles. Helps build rapport with colleagues. Relaxes your eyes and ears. Relaxes your neck. </span></span></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: medium 1pt 1pt none solid solid -moz-use-text-color windowtext windowtext;" width="295" valign="top">\r\n<div class="MsoNormal"><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Drink lots of </span></span><strong><span style="font-family: Trebuchet MS; color: red;"><span style="font-weight: bold; color: red; font-family: ''Trebuchet MS'';">water / liquids</span></span></strong><span style="font-family: Trebuchet MS; color: #333333;"><span style="color: #333333; font-family: ''Trebuchet MS'';">. </span></span></div>\r\n</td>\r\n<td style="border: medium 1pt 1pt medium none solid solid none -moz-use-text-color windowtext windowtext -moz-use-text-color;" width="295" valign="top">\r\n<div class="MsoNormal"><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Will require you to walk to the rest room regularly. Good stress buster </span></span></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: medium 1pt 1pt none solid solid -moz-use-text-color windowtext windowtext;" width="295" valign="top">\r\n<div class="MsoNormal"><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Small meals at regular intervals </span></span></div>\r\n</td>\r\n<td style="border: medium 1pt 1pt medium none solid solid none -moz-use-text-color windowtext windowtext -moz-use-text-color;" width="295" valign="top">\r\n<div class="MsoNormal"><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Keeps away acidity. Makes you walk to the cafeteria thus providing exercise. </span></span></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: medium 1pt 1pt none solid solid -moz-use-text-color windowtext windowtext;" width="295" valign="top">\r\n<div class="MsoNormal"><strong><span style="font-family: Trebuchet MS; color: red; font-size: small;"><span style="font-weight: bold; font-size: 12pt; color: red; font-family: ''Trebuchet MS'';">Laughter</span></span></strong><span style="font-family: Trebuchet MS; color: #333333;"><span style="color: #333333; font-family: ''Trebuchet MS'';"> is the best medicine </span></span></div>\r\n</td>\r\n<td style="border: medium 1pt 1pt medium none solid solid none -moz-use-text-color windowtext windowtext -moz-use-text-color;" width="295" valign="top">\r\n<div class="MsoNormal"><span style="font-family: Trebuchet MS; color: #333333; font-size: small;"><span style="font-size: 12pt; color: #333333; font-family: ''Trebuchet MS'';">Hard works is good, but let us not lose our sense of humour. Learn to laugh for everything. Stress buster, good for face muscles and eyes. Sharpens thinking capabilities. Mainly, laughter is contagious. </span></span></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: medium 1pt 1pt none solid solid -moz-use-text-color windowtext windowtext;" width="295" valign="top">\r\n<div class="MsoNormal"><strong><span style="font-family: Trebuchet MS; color: red; font-size: small;"><span style="font-weight: bold; font-size: 12pt; color: red; font-family: ''Trebuchet MS'';">20, 20, 20, 20 rule</span></span></strong><span style="font-family: Trebuchet MS; color: #333333;"><span style="color: #333333; font-family: ''Trebuchet MS'';"> - Every <strong><span style="text-decoration: underline;"><span style="font-weight: bold;">20 </span></span></strong>minutes, look at an object at least <strong><span style="text-decoration: underline;"><span style="font-weight: bold;">20</span></span></strong> meters away for at least <strong><span style="text-decoration: underline;"><span style="font-weight: bold;">20</span></span></strong> seconds. After that blink your eyes <strong><span style="text-decoration: underline;"><span style="font-weight: bold;">20 </span></span></strong>times before you resume on the computer</span></span><span style="font-family: Trebuchet MS;"><span style="font-family: ''Trebuchet MS'';"> </span></span></div>\r\n</td>\r\n<td style="border: medium 1pt 1pt medium none solid solid none -moz-use-text-color windowtext windowtext -moz-use-text-color;" width="295" valign="top">\r\n<div class="MsoNorma