INSERT INTO `jos_content` VALUES (72, 'The Translator', 'the-translator', '', '<div style="text-align: left;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A New York judge is ready to go through the day''s business and he is very rushed.</span></span></div>\r\n<div style="text-align: left;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">The first case up involves an elderly Jewish gentleman with a long beard, payos, the works.</span></span></div>\r\n<div style="text-align: left;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">The judge, without asking a question, says to the clerk: "Quick...get me a translator."</span></span></div>\r\n<div style="text-align: left;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Translator shows up.\r\n', '\r\n<br /><span style="font-family: comic sans ms,sans-serif;">The judge says</span>: "Ask him what his name is, how old is he and where does he come from?"</span></span></div>\r\n<div style="text-align: left;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;">Translator :</span> "Die judge vilt vissen, vos is dein namen, vie alt bist du, and fun vie kumst du?"</span></span></div>\r\n<div style="text-align: left;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;">The old man : </span>smiles, looks at the judge and says in perfect English with a British accent: "Your Honour. My name is Sir Chaim Ginsbug. I shall be 82 next Thursday and I''ve come from England where I hold the chair of Hebrew Philosophy at Oxford University."</span></span></div>\r\n<div style="text-align: left;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;">The translator : </span>Turns to the judge and says: "Ehr zukt, ehr is Sir Chaim Ginsburg, ehr is tzwei und achtzig yur alt, und ehr is, mit sach Yiddish philisoph, areingekummen fun Oxford."</span></span></div>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /></span></span></p>', 1, 2, 0, 4, '2009-12-30 17:04:55', 62, '', '2010-04-23 07:33:33', 62, 0, '0000-00-00 00:00:00', '2009-12-30 17:04:55', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 11, '', '', 0, 9, 'robots=\nauthor=');
