INSERT INTO `jos_content` VALUES (128, 'ugadi pachadi', 'ugadi-pachadi', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A sweet and sour pachadi or chutney made of tantalizing ingredients like raw mango, tamarind, jaggery and neem flowers. \r\n', '\r\n</span><br /><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:<br /></strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp neem flower</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tsp chrushed jaggery (gur)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">one small lemon size ball of tamarind made into thick tamarind water (imli)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp red chilli powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp mustard seeds(rai dana)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp finely chopped raw mango (kachcha aam)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 c water</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">First add the raw mango pieces to the tamarind water and bring it to boil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Boil it till mango pieces are tender</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Now to this add jaggery and let it cook till it melts and blends into the mixture</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Remove from heat and keep aside</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Now in a pan, heat oil and add mustard seeds let them splutter, add neem flower and fry till light brown</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Now add chilli powder and salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix well</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Take this neem flower tadka and add it to the tamarind and mango juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve in small quantities as an accompaniment to the main course food.</span></span></li>\r\n</ol>', 1, 1, 0, 9, '2010-04-25 09:17:31', 62, '', '2010-05-27 12:05:40', 62, 0, '0000-00-00 00:00:00', '2010-04-25 09:17:31', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 6, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (129, 'Bread Kofta in Pumpkin Curry', 'bread-kofta-in-pumpkin-curry', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Rich in Calcium &amp; Iron - Amazingly tasty steamed koftas cooked in a low calorie gravy made of pumpkin, vegetables and low fat curds.</span></span></p>\r\n', '\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong><br /><br /><span style="font-family: comic sans ms,sans-serif;"><strong>For the koftas</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 6 brown bread slices</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> ¼ cup fresh low fat curds (dahi)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2 tbsp chopped coriander</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2 green chillies, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Salt to taste</span></span></li>\r\n</ol>\r\n<p><strong><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;">For the pumpkin curry</span></span></strong></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1½ cups chopped red pumpkin (kaddu)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 large tomatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup grated onions</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp red chilli powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp coriander-cumin seed (dhania-jeera) powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ tsp turmeric powder (haldi)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp garam masala</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¾ cup fresh low fat curds (dahi)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup boiled green peas</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup boiled small potatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">For the garnish</span></strong><br /></span></span></p>\r\n<ul>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2 tbsp chopped coriander</span></span></li>\r\n</ul>\r\n<p><strong><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;">Procedure</span></span></strong><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong><br /></strong></span></span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>The bread koftas</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove the crust from the bread slices.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Soak the bread in the curds for 20 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the coriander, green chillies and salt and mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Divide it into 18 equal portions and shape into marble-sized balls. Steam in a steamer for 10 minutes or microwave for 20 seconds. Keep aside.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;"><strong>The pumpkin curry</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix the pumpkin with 1 cup of water and cook. When cooked, blend in a liquidiser to make a purée. Keep aside.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Put the tomatoes in hot water for 10 minutes. Remove the skin and grate into a purée.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat the oil in a non-stick pan and fry the onions until light brown in colour.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the red chilli powder, coriander-cumin seed powder, turmeric powder and garam masala and fry for a few seconds.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the curds, pumpkin purée, tomato purée, ½ cup of water and cook for a few minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the green peas, potatoes and salt and mix well.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><strong><span style="font-family: comic sans ms,sans-serif;">Serve</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Just before serving, add the koftas to the curry and bring to a boil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve hot garnished with coriander.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;">Serves :  Six<br /></span></span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><strong><span style="font-family: comic sans ms,sans-serif;">Nutritive values per serving :</span></strong></span></span></p>\r\n<p style="padding-left: 30px;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong></strong><br />Energy: 144 kcal.<br />Fat : 2.8 gm.<br />Carbohydrate: 25.0 gm<br />Calcium: 72.7 gm<br />Iron: 1.1 mg<br />Protein: 4.7 gm<br /></span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /></span></span></p>', 1, 1, 0, 1, '2010-04-25 14:07:08', 62, '', '2010-04-25 14:16:46', 62, 0, '0000-00-00 00:00:00', '2010-04-25 14:07:08', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 16, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (133, 'Amiri Khaman', 'amiri-khaman', '', '<p><span style="font-family: book antiqua,palatino;">A delicious spicy snack from the traditional gujrathi households. It can be described as tempered yellow dhokla </span><span style="font-size: 8pt; font-family: Verdana;"><span style="color: #666666;"><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> \r\n', '\r\n</span></span></span></span><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 Cups of Bengal Gram </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp Sour Curd</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Green Chillis 8 (Nos.) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Ginger 1 piece </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp Turmeric Powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp Soda Bi Carbonate <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp Sugar </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp Lemon Juice<br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste </span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>For Vaghar</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp Oil </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp Mustard Seeds<br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">8 Cloves Garlic (Peeled &amp; Coarsely Crushed) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A Big Pinch of Asfoetida [ Hing ]</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong><span style="font-size: small;">Garnishing</span></strong></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 Cup Grated Coconut</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp Chopped Coriander leaves </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 1/2 Cups fine sev<br /></span></span></li>\r\n</ol>\r\n<p><strong><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"> Procedure :</span></span></strong></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Pick wash and soak the dal in water for about 6 hours. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Drain and grind coarsely to form a thick mixture. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the curd and set aside for about 4 hours to ferment <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">After fermentation, add the ground green chilli-ginger paste, turmeric powder, salt and soda bi carbonate. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Spread this mixture in a greased thali with high edges. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Steam for about 20 minutes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Test by piercing with a knife. This should come out clean. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cool the cooked mixture. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove from the thali using a knief and crumbled by hand. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the sugar and lemon juice (medium sized lemon). Mix well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat the oil . <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the mustard seeds. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When they splutter, add the garlic . <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fry briefly and add the asafoetide (Hing). <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Pour this mixture on the crumbled mixture. Mix well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cover and set aside at room temperature till you are ready to serve. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Just before serving. Garnish with the coconut (greated) coriander leaves (chopped) and sev.</span></span></li>\r\n</ol>', 1, 1, 0, 1, '2010-04-25 14:43:54', 62, '', '2010-05-27 12:28:54', 62, 0, '0000-00-00 00:00:00', '2010-04-25 14:43:54', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 6, 0, 12, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (134, 'Undhia', 'undhia', '', '<p><span style="font-family: book antiqua,palatino;">This is a festive dish usually made during special occasions and is  served with a variety of other items like Khaman Dholka, puri,  kadi,pulao etc. \r\n', '\r\n</span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Papri gm 200 </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Kand gm 200 </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Brinjal (small)  6 </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Potato (small)  4 </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Sweet Potato (medium)  1 </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Raw Banana  1 </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Green Peas cup 1/2 </span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><strong><span style="font-family: comic sans ms,sans-serif;">For Masala</span></strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp asafoetida </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp coriander-cummin powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp garam masala </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup jaggery </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp seasame seeds </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">8 green chillies </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 piece ginger </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups grated coconut </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbsp oil </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">For Vaghar</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup oil </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp asafoetida </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 tsp mustard seeds </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 cups water</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><strong><span style="font-family: comic sans ms,sans-serif;">For Muthias</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 cup gram flour </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 wheat flour </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup chopped methi leaves </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp turmeric powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 chilli powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 garam masala </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp oil </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A little water (if required ) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Oil for deep frying </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong></strong>1.    First prepare the muthias. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine all the ingrediants mentioned under muthias except water and the oil for deep frying . <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add very little water and prepare a stiff dough . <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Make small lemon-sized balls from the dough . <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat oil in a pan and deep fry the balls over a medium flame until golden brown on all sides. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Drain and set aside. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">String the papris (tender) and split into two . <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel and cut the kandh into medium-sized pieces. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel the potatoes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Slit the peeled potatoes and the brinjals into four ,keeping the base intact.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut the sweet potato (optional) and raw banana (optional) ,without peeling ,into 4 pieces each.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash all the vegetables separately and set aside.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix all the ingrediants mentioned under masala.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Stuff the potatoes and brinjals with this masala.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Rub the same masala on all the other vegetables separately. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat the 1/2 cup oil mentioned under vaghar ,in a thick ,broad pan.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the asafoetida and mustard seeds.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When the mustard seeds splutter ,add 3 cups water.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bring to a boil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Reduce flame and arrange all the vegetables in layers.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">First arrange the papris and green peas or toovar seeds.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Next arrange the kandh ,followed by the potatoes ,brinjals ,sweet potatoes and raw banana.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook covered on a low flame for about 30 minutes ,stirring 3 or 4 times very carefully ,so that the vegetables are mashed.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Now arrange the fried muthias on top of the vegetables and cook covered on a low flame for about 5 minutes or until the vegetables are cooked.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">However ,make sure the undhia is not very dry but has just a little gravy.</span></span></li>\r\n</ol>', 1, 1, 0, 1, '2010-04-25 14:53:18', 62, '', '2010-04-25 15:25:55', 62, 0, '0000-00-00 00:00:00', '2010-04-25 14:53:18', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 11, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (135, 'Mysore Pak', 'mysore-pak', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span>:<br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup Bengal Gram</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups Sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup Ghee</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup Water</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Preparation</span></strong>:<br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix the flour with a little ghee. Heat the sugar in water till reaches a ball consistency. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Slowly add the flour stirring continuously so that no lumps are formed. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When it is well blended pour in slowly absolutely in a hot ghee stirring continuously. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook till the mixture becomes frothy and the ghee separates. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Spread out on a greased plate. When firm cut the mysore pak into squares.</span></span></li>\r\n</ol>', 1, 1, 0, 2, '2010-04-25 16:13:46', 62, '', '2010-05-27 12:33:15', 62, 0, '0000-00-00 00:00:00', '2010-04-25 16:13:46', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 7, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (136, 'Rava Laddoo with Khoya', 'rava-laddoo-with-khoya', '', '<p><span style="font-family: book antiqua,palatino;"><span style="font-size: small;"><br /><span style="font-family: comic sans ms,sans-serif;">Ingredients</span>: <br /></span></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">200 gm rava (semolina) </span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">200 gm powdered sugar</span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">200 gm khoya (grated)</span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">100 gm ghee </span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">1 tsp cardamom powder </span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">1 tbsp chopped dried fruits </span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">silver leaf</span></span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><span style="font-size: small;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong>: <br /></span></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">Heat 1 tbsp ghee in a pan and roast the rava over a low flame until golden in color. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">Heat the rest of the ghee and saute the khoya till it collects together (stir continuously). <br /></span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">When the khoya cools, blend all the ingredients together and make small balls. <br /></span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">Garnish with chopped nuts and decorate with silver leaf.</span></span></li>\r\n</ol>', 1, 1, 0, 2, '2010-04-25 16:18:17', 62, '', '2010-05-27 12:32:52', 62, 0, '0000-00-00 00:00:00', '2010-04-25 16:18:17', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (137, 'Coconut Burfi', 'coconut-burfi', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong>: <br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup grated coconut</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp ghee</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 cup khoya </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup water</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Method</span></strong>: <br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat ghee and khoya and saute till the khoya looks of a uniform consistency and looks slightly fried (drying off the extra moisture). <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Take it off the heat mix in the coconut and leave to cool.  \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In another pan heat the sugar and water together over low heat, till sugar dissolves, taking care not to let it come to boil. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Let it come to a boil over high heat now, and cook till thick, and a drop of it dropped on a cold surface or a cup of cold water, sets at once, into a firm, but not hard, heap (soft ball). <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix immediately into the cooled khoya mixture, stirring vigorously, to blend well, and transfer on to the greased plate, pat to level it, and leave to cool and set. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut into desired sized pieces, using a sharp knife and serve</span></span></li>\r\n</ol>', 1, 1, 0, 2, '2010-04-25 16:18:17', 62, '', '2010-05-27 14:26:59', 62, 0, '0000-00-00 00:00:00', '2010-04-25 16:18:17', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 6, 0, 1, '', '', 0, 27, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (138, 'Pickled Paneer Paratha', 'pickled-paneer-paratha', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups paneer </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 cup onions, chopped </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2 tbsp mixed pickle, chopped aa</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp fennel or sonf </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2 tsp cumin or jeera seeds </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp mustard or rai seeds </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 100 gm flour </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tbsp green coriander, chopped </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2 pieces whole red chilly, chopped </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tbsp oil \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure<br /> </strong></span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Stuffing </strong></span><span style="font-family: book antiqua,palatino;"><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Grate the paneer and keep aside. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Heat oil. Add fennel, cumin, mustard. Add the onions and saute. Toss in mixed pickle, coriander and paneer. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix well and divide into 10 equal portions. </span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> <span style="font-family: comic sans ms,sans-serif;"><strong>Dough </strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add water and salt to the flour. Knead into soft dough. Cover with a wet muslin cloth and keep aside for 15 minutes. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Knead dough into peda-sized (one inch diameter) balls. Flatten each shape in the palm of your hand. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place a little of the paneer mixture in the centre and then shape into a round ball. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Roll each ball like a chapatti; keep it three inches in diameter. Shallow fry on a tawa</span></span></li>\r\n</ol>\r\n<p> </p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> </span></span></p>', 1, 1, 0, 3, '2010-04-25 16:30:57', 62, '', '2010-05-27 12:31:18', 62, 0, '0000-00-00 00:00:00', '2010-04-25 16:30:57', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 11, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (139, 'Mixed Vegetable Paratha', 'mixed-vegetable-paratha', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 3 cups mixed vegetables (peas,carrots,beans,) boiled </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 cup potato, boiled and mashed </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Half cup paneer, mashed </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tbsp green coriander chopped </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Half tbsp green chilli chopped </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Half tsp red chilli powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Salt </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> A pinch of sugar </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp dried mango powder or aamchur </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2 tbsp oil \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 100 gms white flour </span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><br /> <strong>Procedure</strong></span><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><br /></span><strong><span style="font-family: comic sans ms,sans-serif;">Stuffing </span><br /> </strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat oil. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add green chilly and allow it to splutter few minutes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the boiled vegetable mash. Saute for five minutes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add mashed potatoes. Add red chilly, salt and aamchur. Saute for another five minutes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add green coriander and paneer. Mix well. Divide into 14 portions. </span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Dough </span><br /> </strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add water and salt to the flour. Knead into soft dough. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cover with a wet muslin cloth and keep aside for 15 minutes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Knead dough into peda-sized (one inch diameter) balls.<br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Flatten each shape in the palm of your hand. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place a little of the vegetable mixture in the centre and then shape into a round ball. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Roll each ball like a chapatti; keep it three inches in diameter. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Shallow fry on a tawa. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve hot</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 3, '2010-04-25 16:57:23', 62, '', '2010-05-27 12:30:58', 62, 0, '0000-00-00 00:00:00', '2010-04-25 16:57:23', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 7, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (140, 'Hariyali Paneer Makhni', 'hariyali-paneer-makhni', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Ingredients<br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tbsp Lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 250 gms Cottage cheese (paneer) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp Green chilli paste </span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> For green makhani gravy </span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 4 stalks Green garlic greens (chopped) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 4 stalks Spring onion greens (chopped) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 3 Green cardamoms </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 6-8 Green garlic bulbs (chopped) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp Garam masala powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1/2 tsp Kasuri methi   \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tbsp Ginger paste </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 3 tbsps Mawa/khoya </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 2 tbsps Honey </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 medium sized Capsicums (deseeded &amp; chopped) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1/2 cup Fresh cream </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 4 tbsps White butter </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 Bay leaf </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 5 Cloves </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 6 medium sized Green tomatoes </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tbsp Oil </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 Cinnamon (one inch stick) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste<br /></span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong>Method <br /> </strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Roughly chop green tomatoes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place them in a pan, add a little water and boil. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cover and cook for fifteen to twenty minutes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Strain and blend the tomatoes to a puree. Keep aside in a bowl. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Reserve the cooking liquid. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Marinate paneer pieces with lemon juice, green chilli paste and salt for fifteen minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat oil in a pan, add green garlic greens, capsicum, spring onion greens and sauté.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cool and blend to a puree. Keep aside in a bowl. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat white butter in a pan. Add bay leaf, cloves, cardamoms, cinnamon and sauté. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add chopped green garlic bulbs, ginger paste and sauté. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add khoya and green tomato puree and sauté. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Strain the reserved cooking liquid and add to the gravy. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the capsicum puree and mix. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add garam masala powder, honey, salt and mix. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add marinated paneer pieces and mix. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add fresh cream and stir gently. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Sprinkle kasuri methi and take it off the heat. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix gently and serve hot.salt</span></span></li>\r\n</ol>', 1, 1, 0, 3, '2010-04-25 17:01:30', 62, '', '2010-05-27 14:27:21', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:01:30', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 1, '', '', 0, 31, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (141, 'Khajur Chutney', 'khajur-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients </span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">50 gm pitted dates </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 15 gm seedless raisins </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp zeera powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp chili powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 heaped tsp tamarind concentrate OR </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 3 tbs lemon juice </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 125 ml water </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 3/4 tsp salt </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp soft brown sugar \r\n', '\r\n</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong></span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><br /> </strong>Put all the ingredients in a blender and mix well. Seive and throw out the residue. Serve the strained chutney with kababs, tikkas.</span></span></p>', 1, 1, 0, 19, '2010-04-25 17:24:38', 62, '', '2010-05-27 13:27:00', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:24:38', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 35, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (142, 'Mixed Dal Dosa', 'mixed-dal-dosa', '', '<p><span style="font-family: comic sans ms,sans-serif;"><strong><span style="font-size: small;">Ingredients</span></strong></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup rice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/3 cup each yellow moong, channa, udad dal</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. curds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. soda bicsarb</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">oil to shallow fry</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong>:</span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash rice separately and dals toghether.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Soak in plenty of water and keep aside for 5-6 hours.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wet grind the rice till semolina type grain can be felt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wet grind rice till fine.Mix both batters.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the curds, salt, soda and oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix well till fluffy and light.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Keep aside for 3-4 hours before making dosas.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat griddle, pour batter and make as for plain dosas.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve hot with chutney.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Make thin or thick as desired.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> </span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Serves</span></strong>: 8-10 medium sized dosas</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Shelflife</span></strong>: 1 day refrigerated.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Texture</span></strong>: Light and thin, foldable but crisp.</span></span></p>', 1, 1, 0, 19, '2010-04-25 17:26:24', 62, '', '2010-05-27 13:26:42', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:26:24', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 34, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (143, 'Coriender Tomato Chutney', 'coriender-tomato-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fresh coriander leaves(cilantro/parsley) - 1 cup</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Tomato - 1 large</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Grated coconut - 1/2 cup</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">green chillies - 2</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">tamarind - a pinch</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt - to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash fresh coriander leaves and pick the leaves from the stem.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut the tomato into medium sized pieces.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the other ingredients and grind it in the blender into a thick paste. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat 3 spoons oil in a fry pan, add mustard seeds.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When it splatters, add urud dal.Add the ground paste and fry till the raw smell dissappears.</span></span></li>\r\n</ol>', 1, 1, 0, 9, '2010-04-25 17:29:11', 62, '', '2010-05-27 12:05:04', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:29:11', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 7, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (144, 'Coconut Chutney', 'coconut-chutney', '', '<p><strong><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;">Ingredients</span></span></strong></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups of fresh coconut, shredded.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">10 dry red chillies</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 sprig curry leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A large pinch of hing (asoefetida)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp methi(fenugreek) seeds, <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp udad dal, <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp channa dal<br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small tomato</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n</ol>\r\n<p><strong><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;">Procedure</span></span></strong></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat some oil and add the dhals, curry leaves, hing, methi and red chillies. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fry till done</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add this to the coconut and tomato and blend into a smooth paste. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add salt to taste.</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;">Goes well with dosas, idli and plain rice.</span></span></p>', 1, 1, 0, 9, '2010-04-25 17:30:58', 62, '', '2010-05-27 12:04:45', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:30:58', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (145, 'Coriander Chutney', 'coriander-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span>:</span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 bunch coriander leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2-3 tbsp coconut</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 green chillies</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2-3 dry red chillies, roasted</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A small piece ginger</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2-3 garlic flakes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small onion</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Juice of 1/2 a lemon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong> :</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Blend all the above to a smooth paste.\r\n', '\r\nAdding a little water.</span></span></p>', 1, 1, 0, 9, '2010-04-25 17:34:05', 62, '', '2010-05-27 12:04:22', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:34:05', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (146, 'Baigan ka Bharta', 'baigan-ka-bharta', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 medium eggplants</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 Small onion (chopped)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 medium tomato (chopped)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 fresh green chili (change quantity according to taste)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup plain yogurt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoon garlic paste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon ginger paste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon turmeric powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoons garam masala powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons mango powder\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoon oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong> <span style="font-family: comic sans ms,sans-serif;">Procedure</span> </strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Make slits in eggplants and roast them in a 400 degree oven for 10-15 minutes or till soft. (Alternatively, cut and cook over stove with oil)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel and mash them.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat the oil in medium heat. Add the half of onion and fry until transparent.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add turmeric, salt and yogurt. Cook for few minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the eggplant, tomato, garam masala, green chili and mango powder. Cook for 5 to 10 minutes with continuous stirring.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove from heat. Add the rest of onions and mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve hot.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 17:35:44', 62, '', '2010-05-27 13:26:24', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:35:44', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 33, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (147, 'Onion Chutney', 'onion-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span> </strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup red onions (finely chopped)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup tomato paste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon paprika</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ teaspoon cumin powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ teaspoon oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ teaspoon sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste (~ ½ teaspoon)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ teaspoon garlic paste (Optional)<br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cilantro for garnish (Optional)<br /></span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong> <span style="font-family: comic sans ms,sans-serif;">Procedure</span> </strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat the oil for few seconds.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Put everything except onion (and cilantro) in oil. Simmer sauce for few minutes and let it cool.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix sauce with onion (and garnish with cilantro). Serve chilled.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 17:37:56', 62, '', '2010-05-27 13:25:31', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:37:56', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 32, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (148, 'Tamarind Chutney', 'tamarind-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">200gms Tamarind (Imli)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">300gms Jaggery (Gur) grated</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp Roasted cumin (Jeera) Powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp Red chili powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt To Taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp Black salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp Garam masala</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong><span style="font-size: small;">Procedure</span></strong></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add 5 cups of water to tamarind and bring to er the flame and cook for 10 minutes. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Strain it and add the jaggery, chilli powder, cumin powder, salt, garam masala and mix well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook again on medium flame till jaggery dissolves completely and the chutney gets semi thick(not too thick) <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Take out the pulp from the tamarind mixture.This chutney thickens more on cooling so check for consistency when using.</span></span></li>\r\n</ol>', 1, 1, 0, 9, '2010-04-25 17:43:21', 62, '', '2010-05-27 14:27:39', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:43:21', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 1, '', '', 0, 22, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (149, 'Lemon Pickle', 'lemon-pickle', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span>:</span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 kg Lemons (Nimbu)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">250 grams Sugar (Cheeni) (2 cups heaped)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon Red chili pepper (Lal Mirchi)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons Salt (Namak)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 teaspoon ground big Cardamoms (Elaichi Moti)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/8 teaspoon ground Cloves (Lavang)</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong><span style="font-size: small;">Procedure</span></strong></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash the lemons and wipe with a cloth. Squeeze out the juice and add salt to it.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut long strips of lemon skins and soak in the juice and put in a jar. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Put the jar in the sun for 6 days and shake it every other day till the lemon skins are tender. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add sugar, Red chili pepper, cardamoms and cloves and mix.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Keep it in the sun until sugar is dissolved.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">This chutney can be preserved for one year if it is kept in an airtight jar.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 17:45:16', 62, '', '2010-05-27 13:25:09', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:45:16', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 31, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (150, 'Mint Chutney', 'mint-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong>:</span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 bunch fresh mint leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 green chillies</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp tamarind pulp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt To Taste, little water</span></span></li>\r\n</ol>\r\n<p><strong><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;">Procedure</span></span></strong></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">To make tamarind pulp soak little tamarind in water and keep aside for 2 hours. Then strain the mixture in a bowl with the help of a spoon. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">To make tamarind juice add little water to the pulp. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash the mint leaves well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Grind it to a smooth paste with green chillies, salt and tamarind juice.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">You can add water if the mixture is thick.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 17:47:35', 62, '', '2010-05-27 13:24:46', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:47:35', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 30, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (151, 'Peanut Chutney', 'peanut-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong>:</span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup peanut</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3-4 green chilies</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Few coriander leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp cumin powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to Taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Sugar or jaggery as per taste</span></span></li>\r\n</ol>\r\n<p><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Soak peanuts in little water for half an hour. Shell them and keep aside. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Chop the green chilies. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Blend the peanuts and chilies. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add salt, sugar or jaggery and cumin powder and mix again. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When serving add lemon juice.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 17:49:56', 62, '', '2010-05-27 13:24:16', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:49:56', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 29, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (152, 'Mint-Tamarind Chutney', 'mint-tamarind-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span>:</span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">handful of Mint Leaves (Pudina Leaves)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 Green Chillies (Hari Mirch)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup Tamarind (Imli)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt (Namak) to taste</span></span></li>\r\n</ol>\r\n<p><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Grind the green chillies and mint leaves into a fine paste.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Soak tamarind for 15 minutes in 1/2 cup of hot water and then pass through a sieve.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix the tamarind pulp and salt with the ground paste</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 17:51:46', 62, '', '2010-05-27 13:23:27', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:51:46', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 28, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (153, 'Chana Dal with Pumpkin', 'chana-dal-with-pumpkin', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong>:<br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup chana dal (Bengal gram)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">250 gm pumpkin</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tomatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 pods of garlic</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ piece of ginger</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 green chillies</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">a bunch of curry leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 onion</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp turmeric powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp oil\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp tamarind pulp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong>:<br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Chop the onion and tomatoes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Slit the chillies into halves.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel the pumpkin and remove the pith and cut the pumpkin into small pieces.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel and grate the ginger.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel and crush the garlic.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook the chana dal in 4-5 cups of water till tender but not overcooked.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Keep aside.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat the oil and fry the onion till it is light golden brown.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the ginger, garlic and the tomatoes and fry for another minute.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix in the pumpkin pieces and salt to taste and the turmeric powder.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add one cup of water and cover with a lid and cook over a medium heat till the pumpkin pieces are tender.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix in the cooked dal and allow the dal to simmer.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the green chillies and the curry leaves.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Continue to simmer for 3-4 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix in the tamarind pulp and cook for another minute.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve hot with rice or paranthas.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 17:55:50', 62, '', '2010-05-27 13:23:04', 62, 0, '0000-00-00 00:00:00', '2010-04-25 17:55:50', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 27, '', '', 0, 5, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (154, 'Garlic Chutney', 'garlic-chutney', '', '<p><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;">A must have for any chat</span><strong><span style="font-family: comic sans ms,sans-serif;">\r\n', '\r\n</span></strong></span></p>\r\n<p><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong><br /></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">1 cup garlic cloves, peeled</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 tablespoon chilli powder</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">juice of 1/2 lemon</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">salt to taste</span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><br /><strong><span style="font-family: comic sans ms,sans-serif;">Method</span></strong><br /></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">Combine all the ingredients in a blender with ¼ cup of water and grind to a fine paste.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Before using, dilute with water and use as required.</span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 18:41:25', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-25 18:41:25', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 26, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (155, 'Khajur Imli Chutney', 'khajur-imli-chutney', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">This sweet and sour chutney blend spruces up almost all chaat recipes. \r\n', '\r\n<span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups dates (khajur), deseeded</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup tamarind (imli), deseeded</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup jaggery (gur), grated</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon chilli powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">a pinch asafoetida (hing)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash the dates and tamarind and place them in a saucepan.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the jaggery, chilli powder, asafoetida, salt and 4 cups of water and simmer for 20 to 25 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cool and strain the mixture through a sieve.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Use as required. Store refrigerated.</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>Tip</strong> : You can also purée</span></span></p>', 1, 1, 0, 19, '2010-04-25 18:44:13', 62, '', '2010-05-27 13:22:36', 62, 0, '0000-00-00 00:00:00', '2010-04-25 18:44:13', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 25, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (156, 'Green Chutney (Mint)', 'green-chutney', '', '<p><span style="font-family: book antiqua,palatino;">A green mint and coriander flavoured chutney which is great for sandwich spreads  \r\n', '\r\n<span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">2 cups chopped mint leaves</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 cup chopped coriander</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 large onion, sliced</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">juice of 1 to 2 lemons</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 tablespoon sugar</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">4 to 6 green chillies</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">salt to taste</span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong><br /></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">Combine all the ingredients and grind to a smooth paste in a blender using very little water.</span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>Tip : </strong>Refrigerate and use as required.</span></p>', 1, 1, 0, 19, '2010-04-25 18:47:12', 62, '', '2010-05-27 13:22:25', 62, 0, '0000-00-00 00:00:00', '2010-04-25 18:47:12', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 24, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (157, 'Green Chutney (Coriander)', 'green-chutney-coriander', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 bunch coriander (hara dhaniya)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2-3 green chili hot one</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small onion</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small tomato</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to tast</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3-4 tbp tamarind(imli)pulp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Garlic 3-4 cloves</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span></span><span style="font-family: book antiqua,palatino;"><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash coriander and green chili</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">cut onion in to 4 pieces (remove skin)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">cut tomato in to 4 pieces\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">skin off garlic then put all ingredients in blender jug including imli pulp , salt, zeera</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add little water and blend this all fine, smooth sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">serve with appetizer , sandwich or fried fish</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>Tip :</strong> you can save this chutney for weeks in your refrigerator</span></span></p>', 1, 1, 0, 19, '2010-04-25 18:50:51', 62, '', '2010-05-27 13:22:00', 62, 0, '0000-00-00 00:00:00', '2010-04-25 18:50:51', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 23, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (158, 'Turiya Mag Ni Daal', 'turiya-mag-ni-daal', '', '<p><span style="font-family: book antiqua,palatino;">Ridge gourd cooked with moong dal to make a different vegetable preparation.\r\n', '\r\n</span></p>\r\n<p><span style="font-family: book antiqua,palatino;"><strong> </strong></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">250 grams ridge gourd (turiya), peeled and diced</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 cup split yellow gram (yellow moong dal)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 teaspoon cumin seeds (jeera)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2 teaspoon asafoetida (hing)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 teaspoon green chilli-ginger paste</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/4 teaspoon turmeric powder (haldi)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/4 teaspoon sugar</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 teaspoon lemon juice</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 tablespoon oil</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">salt to taste</span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><strong> </strong></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">Soak the yellow moong dal in warm water for at least 30 minutes. Drain and keep aside.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Heat the oil, add the cumin seeds and asafoetida and fry till the seeds crackle.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Add the ridge gourd and soaked moong dal and stir for a few seconds.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Add 3 to 4 cups of water, the green chilli-ginger paste, turmeric powder, sugar and salt and simmer till ridge gourd is tender.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Add the lemon juice and serve hot.</span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>Tip:</strong> The peel of the ridge gourd can be added to green chutney as it enhances the nutritional value.</span></p>', 1, 1, 0, 1, '2010-04-25 18:52:35', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-25 18:52:35', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 10, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (159, 'Walor Muthia Nu Shaak', 'walor-muthia-nu-shaak', '', '<p><span style="font-family: book antiqua,palatino;">A delicious vegetable preparation of walor papdi and fenugreek dumplings. \r\n', '\r\n</span></p>\r\n<p><span style="font-family: book antiqua,palatino;"><strong> </strong></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>For the methi muthias</strong></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">1 bunch fenugreek leaves (methi), chopped</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2 cup whole wheat flour (gehun ka atta)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2 cup gram flour (besan)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 teaspoon green chilli-ginger paste</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">3 teaspoons sugar</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">juice of 1 lemon</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2 teaspoon garam masala</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2 teaspoon turmeric powder (haldi)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">a pinch soda-bi-carb</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">3 tablespoons oil</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">salt to taste</span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><strong> </strong></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>Other ingredients</strong></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">400 grams walor papdi</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 teaspoon carom seeds (ajwain)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 teaspoon mustard seeds</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2 teaspoon asafoetida (hing)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">2 teaspoons green chilli-ginger paste</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 teaspoon turmeric powder (haldi)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 tablespoon coriander-cumin seed powder (dhana-jeera)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2 teaspoon chilli powder</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">2 teaspoons sugar</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">3 tablespoons oil</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">salt to taste</span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><strong> </strong></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>Method</strong></span></p>\r\n<p><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">For Muthias</span><br /></strong></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">Put the chopped methi leaves in a bowl, add 1 teaspoon of salt and mix well.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Allow to stand for 5 to 7 minutes and squeeze out all the liquid from the methi leaves.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Combine with the remaining ingredients in a bowl and knead into a soft dough, adding water only if required.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Divide this mixture into 30 portions and flatten by placing between the palms. Keep aside.</span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"> </span></p>\r\n<p><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Rest of the recipie</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">Wash and string the walor papdi.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Heat the oil in a pan, add the carom seeds, mustard seeds and asafoetida. When the mustard seeds crackle, add the walor papdi and 3 to 4 cups of water.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Add the green chilli-ginger paste, turmeric powder, coriander-cumin seed powder, chilli powder, sugar and salt. <br /></span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Cover and simmer till the walor papdi is almost cooked.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Gently add in one muthia at a time.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Cover and simmer for 10 to 15 minutes till the muthias are cooked.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Serve hot with parathas.</span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;">Serves 4 to 6. <br /></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong>Tips: </strong>You can also deep fry the muthias and then add them to the vegetables.</span></p>', 1, 1, 0, 1, '2010-04-25 18:54:36', 62, '', '2010-05-27 12:27:19', 62, 0, '0000-00-00 00:00:00', '2010-04-25 18:54:36', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 9, '', '', 0, 5, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (160, 'Carrot Methi Curry', 'carrot-methi-curry', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cup Carrots, cut into cubes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cup Fenugreek Leaves (methi), chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp Cumin Seeds (jeera)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup Onions, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 to 4 Green Chilies, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 large Garlic Clove, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">12-mm piece Ginger, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ tsp Turmeric Powder (haldi)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp Coriander Powder (dhania)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp Oil\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt (to taste)</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong><span style="font-family: book antiqua,palatino;"><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Take a non-stick pan and heat oil in it. Add cumin seeds.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When the seeds start to crackle, put onions, green chilies, garlic and ginger in it and saute for 2 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Now, add fenugreek leaves and saute for 2 more minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add carrots, turmeric powder, coriander powder, salt and a cup of water to this mixture and stir.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook this mixture on slow flame and let the moisture evaporate from it. Make sure that the carrots are tender.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve hot with rotis.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 19, '2010-04-25 18:57:25', 62, '', '2010-05-27 13:20:39', 62, 0, '0000-00-00 00:00:00', '2010-04-25 18:57:25', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 22, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (161, 'Kashmiri Dum Aalu', 'kashmiri-dum-aalu', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span>: </strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 kg Small Potatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A pinch of Asfoetida</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp Turmeric Powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp Chilli powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 Medium size onion (grated)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cup Fine Curd</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4-5 Bay Leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp Ginger garlic paste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbsp Ghee or oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup Milk\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>For Masala: </strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 Cardamoms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">7-8 Black Peppers</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp Coriander Seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp Caraway Seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4-5 Cloves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 Small piece of Cinnamon</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> <span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Make a fine powder by blending together the ingredients of the masala in a blender. Keep it aside.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove the peel from potatoes and prick them.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Take oil in a frying pan and heat it for about one minute. Fry the potatoes on a medium heat until their color changes from white to light brown. Keep it aside.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add grated onion, asafoetida, bay leaves and ginger-garlic paste to the remaining oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fry the mixture till the paste turns reddish brown.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the masala prepared and heat for about 2 to 3 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add turmeric, chilli powder and salt.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fry again for about a minute.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Now pour the milk to make gravy followed by adding fine curd.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Keep stirring continuously, add water if the gravy appears too thick.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Now add pricked potatoes to this gravy and cook in a pressure cooker. Remove from the flame after 4 to 5 minutes just before the first whistle.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Garnish it with fresh coriander leaves.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Dum Aloo is ready to serve.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 19:00:15', 62, '', '2010-05-27 13:20:16', 62, 0, '0000-00-00 00:00:00', '2010-04-25 19:00:15', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 21, '', '', 0, 7, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (162, 'Palak Kofta', 'palak-kofta', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 bunches of palak(cut ,boiled and churned in mixer)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2-3 onions chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tomatoes chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3-4 pods of garlic chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ kg potatoes boiled and mashed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup fresh cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup raisins</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4-5 green chilles</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp jeera</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp garam masla pwd\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 to 1 tsp chilli pwd</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp black pepper pwd</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup cornflour</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">oil for deep frying<br /></span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add some salt,red chilly pwd,half of green chilles cut,and some black pepper pwd to  the mashed potatao mixture</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the cornflour and mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix equal sized balls of the mixture</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Take one ball at a time,create a hole in it ans fill it with some cream and 2-3 raisins and then reseal it.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Dry fry these balls till brownish in colour.Make sure that the oil is neither too hot nor cold.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In another pan add 2tbsp oil and heat it,then add jeera nd let it splutter.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the garlic to it and fry for some time then add onion and fry  again till golden brown then add tomatoes and fry till the mixture starts to leave oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Then add some salt and red chiily pwd and remaing masalas to it and fry some more.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the palak mixture and fry it till the water evaporates and the mixture leaves oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Finally add the koftas and  and add some garma masala and serve hot with chapatis.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-25 19:03:15', 62, '', '2010-05-27 13:19:57', 62, 0, '0000-00-00 00:00:00', '2010-04-25 19:03:15', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 20, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (163, 'Palak Paratha', 'palak-paratha', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 cup wheat flour</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1/2 cup besan</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1/2 jawar flour</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 chopped onion(big)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 bunch of spinach(palak)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp cumin seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp turmeric</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp red chilly powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Oil to fry</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> salt to taste\r\n', '\r\n</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Wash spinach, cut it &amp; roughly grind it from grinder.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In a bowl, mix all flours(wheat, basan &amp; jawar),add chopped onion, salt, cumin seeds, turmeric &amp; chilly powder. Finally by adding spinach make a dough of the mixture. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Make 7-8 balls of the dough &amp; roll it.make a hole at centre of paratha so as it can cook well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> On a hot griddle(Tawa) put the paratha &amp; fry it by applying oil  on it ,like regular paratha.Do the same for all paratha.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve it with mango or lime pickle.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 19, '2010-04-25 19:05:51', 62, '', '2010-05-27 13:19:37', 62, 0, '0000-00-00 00:00:00', '2010-04-25 19:05:51', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 19, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (164, 'Katori Chat', 'katori-chat', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients</span></strong><br /><strong><span style="font-family: comic sans ms,sans-serif;">For katori</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup plain flour (maida)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup butter, softened</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 teaspoon salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">For the filling </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup mixed sprouts (boiled)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon cumin seeds (jeera)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">a pinch asafoetida (hing)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons chilli powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons coriander-cumin seed (dhania-jeera) powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 teaspoon turmeric (haldi) powder\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon amchur (dry mango powder)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon chole masala (chana masala)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>For serving </strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">beaten curds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">khajur imli ki chutney</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">green chutney</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">sev or nylon sev</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">chaat masala</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure<br /></strong></span></span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>For katori</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine the flour, butter and salt and make a stiff dough using a little water. Knead very well</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Divide the dough into 2 equal portions.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Roll out each dough portion into a sheet of 6 mm. (¼") thickness.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut rounds of pastry using a large 50 mm. (2") diameter cookie cutter and press into tart moulds.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Prick all over with a fork.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake in a hot oven at 230°C (450°F) for 10 to 15 minutes. (or Deep Fry)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cool the tart cases.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><strong><span style="font-family: comic sans ms,sans-serif;">For Stuffing</span></strong><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat the oil in a pan and add the cumin seeds. When they crackle, add the asafoetida.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the sprouts, chilli powder, coriander-cumin seed powder, turmeric powder, amchur, chole masala, salt and 2 tablespoons of water and mix well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook on a slow flame for 4 to 5 minutes. Keep aside.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /><span style="font-family: comic sans ms,sans-serif;"><strong>How to proceed</strong></span><br /></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fill each tart with a portion of the filling.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Top with curds, khajur imli ki chutney, green chutney, sev and chaat masala.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve immediately.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><br />Makes 12 pieces.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Tips</strong> Chole masala is a spice blend that is easily available at provision stores.</span></span></p>', 1, 1, 0, 19, '2010-04-25 19:07:41', 62, '', '2010-05-27 13:19:15', 62, 0, '0000-00-00 00:00:00', '2010-04-25 19:07:41', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 18, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (165, 'Suji Dhokla', 'suji-dhokla', '', '<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>Ingredients</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup Suji</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 Cup Curd (Dahi)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4th tbsp  ENO powder (plain).</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Black pepper Optional</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Red Chilli Optional</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 Tbsp Ginger garlic Green Chilli grinded (Optional)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 Tbsp Oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Curry Leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Popeye Seeds - 1 tbsp\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp - Mustard seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp - Cumin Seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp Sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Coriander Leaves for garnishing.</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>Procedure</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix Suji and Curd and keep it for 1-2 hours for marination. You can also keep it overnight... However 1 hour is good enough.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In a deep vessel keep some water for boiling. While this comes to a boil...</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix Ginger garlic Green Chilli paste , Salt, Black Pepper, Red Chilli (all as per taste ).</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Stir Well. Normally you would not need to add water. But check the consistency if its too thick you may need to add little bit of it. The batter should be of cake batter consistency.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In the end add ENO (Plain) Powder. Stir quickly and Place in a greased thali.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Keep this for Steaming for around 10  min.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Once they are done and cold, cut into pieces.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Now in a pan add some oil --- Mustard seeds, Cumin seeds ... once these are done add popeye seeds and Curry leaves. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In the end add little sugar and  put the Dhokla''s in the pan. Stir for 2-3 min and you are  done.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve this pudina or Coriander chutney<br /></span></span></li>\r\n</ol>', 1, 1, 0, 1, '2010-04-25 19:12:41', 62, '', '2010-05-27 12:26:30', 62, 0, '0000-00-00 00:00:00', '2010-04-25 19:12:41', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 8, '', '', 0, 5, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (166, 'Doodhi Muthiia', 'doodhi-muthiia', '', '<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A steamed farsan made with bottle gourd and 3 kinds of flours and fresh spices. An ideal tea-time snack.<strong><br /></strong></span></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>Ingredients</strong></span></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>For the muthias</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups white pumkin (lauki), grated</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 onion, grated (optional)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup whole wheat flour (gehun ka atta)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 cup semolina (rawa)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup Bengal gram flour (besan)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons green chilli-ginger paste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon turmeric powder (haldi)\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon garam masala</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon cumin seeds (jeera)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon fennel seeds (saunf)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">juice of 1 1/2 lemons</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons chopped coriander</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tablespoons oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon soda-bi-carb</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon asafoetida (hing)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong> </strong></span></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>For the tempering</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons mustard seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tablespoon sesame seeds (til)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon asafoetida (hing)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tablespoons oil</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong> </strong></span></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>For the garnish</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons chopped coriander</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons grated coconut</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong> </strong></span></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>Procedure</strong></span></span></p>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>For the muthias</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Strain the water out of the grated pumpkin and onion, keeping liquid aside to use if required to knead the dough.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine all the ingredients for the muthia in a bowl and form a soft dough adding the strained vegetable liquid if required.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Using oiled palms, divide the mixture into 3 parts and roll out into cylindrical shapes 200 mm. (8") long.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Steam for 25 to 30 minutes till done.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cool and slice into 20 mm. (3/4 ") long pieces and keep aside.</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>How to proceed</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat the oil and add the mustard seeds. When they crackle, add the sesame seeds and asafoetida.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the sliced muthia and stir for 5 to 10 minutes till they are light brown in colour.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Garnish with coriander and coconut.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve hot with green chutney.</span><span style="font-family: book antiqua,palatino;"><br /></span></span> </li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><strong><span style="font-family: comic sans ms,sans-serif;">Tips</span>: </strong><br /></span></span></p>\r\n<ul>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">To check if the muthias are done, insert a toothpick in the centre. If it comes out clean, the muthias are cooked.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">You can use cabbage or methi instead of pumpkin.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serves 4 to 6.</span></span></li>\r\n</ul>', 1, 1, 0, 1, '2010-04-25 19:16:28', 62, '', '2010-05-27 12:26:04', 62, 0, '0000-00-00 00:00:00', '2010-04-25 19:16:28', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 7, '', '', 0, 5, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (167, 'Aalu Methi ki Tikki', 'aalu-methi-ki-tikki', '', '<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>Ingredients</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 kg Potatoes, boiled and mashed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp Kasoori Methi</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">100 gms Fenugreek leaves, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp dried Fenugreek leaf powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">50 gms Breadcrumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsps Cumin powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ tsps Cumin seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ tsp Turmeric powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">100 gms Onions, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp Chaat Masala powder\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp Green Chillies, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp Garam Masala powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">50 gms Coriander leaves, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt according to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">50 ml Oil</span></span></li>\r\n</ol>\r\n<p><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: small;"><strong>Procedure</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat oil, add cumin seeds, chopped onions and green chillies. Stir-fry till onions are translucent.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add mashed potatoes and the rest of the ingredients. Mix well and take off heat. Rest the mixture for an hour.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Shape into round patties of 50 gm each using the palm of the hand.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Shallow-fry in hot oil to a golden colour. Pat-dry with kitchen paper to remove excess oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Garnish with chaat masala powder. Serve hot.</span></span></li>\r\n</ol>', 1, 1, 0, 1, '2010-04-25 19:19:24', 62, '', '2010-05-27 12:24:10', 62, 0, '0000-00-00 00:00:00', '2010-04-25 19:19:24', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 6, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (168, 'Anchovy Antipasto', 'anchovy-antipasto', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup vegetable oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 anchovy fillets, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 can of tuna, drained and mashed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 6 oz. jar marinated artichoke hearts, drained and cut up</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small bag of baby greens</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tomatoes cut into 8 slices each</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">8 slices of pepperoni</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In small bowl combine anchovy, oil and lemon juice. Let stand.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Pour anchovy mixture into larger bowl and add all ingredients except baby greens.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place baby greens on individual dishes and pour mixture ontop of greens.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 06:04:11', 62, '', '2010-05-27 14:14:52', 62, 0, '0000-00-00 00:00:00', '2010-04-27 06:04:11', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 75, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (169, 'Apple and Brie Fondue ', 'apple-and-brie-fondue-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:<br /></strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 lbs. brie cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">5 apples, peeled and cut into slices</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove rind from cheese and cut into cubes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place cheese in fondue dish. Heat until cheese has melted but not liguidy.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve apple slices to dip into cheese. You can use teared pieces of italian bread also.</span></span></li>\r\n</ol>\r\n<p> </p>', '', 1, 1, 0, 20, '2010-04-27 06:07:47', 62, '', '2010-04-27 16:50:52', 62, 0, '0000-00-00 00:00:00', '2010-04-27 06:07:47', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 74, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (170, 'Artichoke Dip', 'artichoke-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:<br /></strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">1 can artichoke hearts, chopped up</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">5 scallions, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 garlic cloves, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup muenster cheese </span><br /> <br /></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Preheat oven to 350 degrees.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Grease small baking dish.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In medium bowl combine all ingredients.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place in greased baking dish and bake for 10 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 06:15:58', 62, '', '2010-05-27 14:14:08', 62, 0, '0000-00-00 00:00:00', '2010-04-27 06:15:58', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 73, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (171, 'Artichokes and Shrimp Dip ', 'artichokes-and-shrimp-dip-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">8 whole artichokes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. rosemary</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 4½ oz. cans shrimp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 cup mayonnaise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbs.. dijon mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. tarragon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 bay leaf\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. Worcestershire sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup onion, minced</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;"> Wash and trim artichokes leaves. Cook artichokes in boiling water. About 1 inch deep. <br /></span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">Add to the water lemon juice, salt, pepper, bay leaf, tarragon and rosemary. <br /></span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">Cook for about 45 minutes or until leaves pull off easily.</span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">To make shrimp sauce you add mayonnaise, mustard, Worcestershire sauce, onions and shrimp. <br /></span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">Mix all ingredients well.</span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">Place shrimp dip in ramekins and artichokes on one plate each.</span></span></li>\r\n<li><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">Makes nice appetizer or first course.<br /></span><br /></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-27 06:20:18', 62, '', '2010-05-27 14:13:09', 62, 0, '0000-00-00 00:00:00', '2010-04-27 06:20:18', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 72, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (172, 'Arugala Salad with Balsamic Vinegar Dressing', 'arugala-salad-with-balsamic-vinegar-dressing', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 garlic cloves, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ lbs. arugala</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 pint cherry tomatoes, halved</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. balsamic vinegar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 shallots, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ tsp. salt</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:<br /></strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In small bowl or blender mix together garlic, balsamic vinegar, olive oil, shallots and salt.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In large bowl add in arugala and tomatoes. Pour over dressing and serve.</span></span></li>\r\n</ol>', '', 1, 1, 0, 20, '2010-04-27 06:23:29', 62, '', '2010-05-27 14:12:09', 62, 0, '0000-00-00 00:00:00', '2010-04-27 06:23:29', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 5, 0, 71, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (173, 'Asparagus Cream Cheese Spread', 'asparagus-cream-cheese-spread', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cans asparagus</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 scallions, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. horseradish sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 slices bacon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">pinch salt</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Cook bacon in microwave or fry. Let cool and break up into tiny small pieces.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place all ingredients into food processor or blender. Mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Refrigerate for 1 hour before serving.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with vegetables, breads or crackers.</span></span></li>\r\n</ol>', '', 1, 1, 0, 20, '2010-04-27 06:26:27', 62, '', '2010-04-27 16:49:33', 62, 0, '0000-00-00 00:00:00', '2010-04-27 06:26:27', '0000-00-00 00:00:00', 