INSERT INTO `jos_content` VALUES (174, 'Baby Reubens', 'baby-reubens', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">40 slices of party rye</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 pkgs. of shredded swiss cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 10 oz. can sauerkraut, rinsed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 lb. sliced corn beef</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">dijon mustard<br /></span><br /></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place bread on baking sheet. Broil for 2 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Arrange corn beef, mustard and sauerkraut on bread.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Broil for 5 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve.</span></span></li>\r\n</ol>\r\n<p> </p>', '', 1, 1, 0, 20, '2010-04-27 09:08:54', 62, '', '2010-04-27 16:49:18', 62, 0, '0000-00-00 00:00:00', '2010-04-27 09:08:54', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_prin