INSERT INTO `jos_content` VALUES (175, 'Bacon and Chunky Clam Dip', 'bacon-and-chunky-clam-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">10 slices well done bacon, crumbled</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 6 oz. cans minced clams</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">8 oz. softened cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 scallions, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. Worcestershire sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">tsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. white horseradish sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup finely chopped red pepper</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Blend all ingredients in medium mixing bowl and refrigerate for 2 hours before serving.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Can be served with torn pieces of italian bread or chips</span></span></li>\r\n</ol>', '', 1, 