INSERT INTO `jos_content` VALUES (176, 'Bacon Wrapped Scallops', 'bacon-wrapped-scallops', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">24 large real scallops, cleaned</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">12 slices of bacon, cut in half</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Garlic Powder</span><br /></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Preheat broiler.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine 1/2 tsp. salt and 1/2 tsp. pepper with 1 tsp. of garlic powder.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wrap scallop with bacon strip and secure with toothpick.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Sprinkle salt mixture on scallop.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place in broiler for 5−8 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 10:18:29', 62, '', '2010-05-27 14:11:13', 62, 0, '0000-00-00 00:00:00', '2010-04-27 10:18:29', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 67, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (177, 'Baked Artichoke Pastry', 'baked-artichoke-pastry', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span>:<br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 can biscuits</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 can artichoke hearts, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup cheddar cheese, shredded</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 green onion, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp. oregano</span><br /></span> </li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span>:<br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Preheat oven as indicated on biscuit can.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine all ingredients in bowl.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Separate biscuits with fingers to make a pocket and stuff with mixture.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake for 10 minutes and serve.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 10:21:24', 62, '', '2010-05-27 14:10:53', 62, 0, '0000-00-00 00:00:00', '2010-04-27 10:21:24', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 66, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (178, 'Baked Artichoke Dip ', 'baked-artichoke-dip-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">1<span style="font-family: book antiqua,palatino;"> Rye or Pumpernickel Bread, Round</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. shredded cheddar cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">7 green onions, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 can artichoke hearts, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. prepared chopped garlic, 4 garlic cloves, chopped</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> P<span style="font-family: book antiqua,palatino;">reheat oven to 350.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Cut medium size hole on top of bread. Remove inside of bread to hollow it out.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In small saute pan cook onions and garlic in 1 tbsp. butter.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In medium bowl combine all ingredients and blend well. Add in the onions and garlic mixture and blend well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Scoop into bread mixture.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place top of bread back on.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Cover bread completely in aluminum foil and bake for 60 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with the pulled out bread or other breads or crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 10:27:15', 62, '', '2010-05-27 14:10:34', 62, 0, '0000-00-00 00:00:00', '2010-04-27 10:27:15', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 65, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (179, 'Baked Clams', 'baked-clams', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">48 littleneck clams</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ cups bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 cloves garlic, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbsp. roasted peppers, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbsp. olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. oregano<br /></span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:<br /></strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">First clean clams. Pry them open with a butter knife (it is safer than steak knife). <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut out the clam and reserve in bowl. Save one side of the clam shell for serving. We will need 48 halves.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In small bowl combine bread crumbs, garlic, peppers, oil and oregano. Mix well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Now with the clams, I prefer to chop up all the clams into little pieces (my preference). If you choose to chop them up mix the chopped clams with other ingredients and spoon mixture into clams shells. If you choose not to, take whole clams and place back in shell and spoon mixture over clam.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place clams on large cookie sheet and bake for about 10 minutes. If you like you can garnish with parmesan cheese and lemon.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Please make sure before placing clam back into shell that you clean them well.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 10:36:09', 62, '', '2010-05-27 14:10:13', 62, 0, '0000-00-00 00:00:00', '2010-04-27 10:36:09', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 64, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (180, 'Barbecue Cocktail Meatballs', 'barbecue-cocktail-meatballs', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">2<span style="font-family: book antiqua,palatino;"> lb. ground beef</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 onion, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup milk</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup barbecue sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup apricot jam</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Preparation:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Combine beef, onion, milk, bread crumbs, eggs and salt. Mix well and make into small balls. Bake in oven for 45 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In large sauce pan add in barbecue sauce, water and jam. Add in meatballs and let simmer on low for 10 minutes.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with toothpicks.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 10:40:30', 62, '', '2010-05-27 14:09:20', 62, 0, '0000-00-00 00:00:00', '2010-04-27 10:40:30', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 63, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (181, 'Barbecue Mini Meatballs ', 'barbecue-mini-meatballs-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 lbs. chopmeat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups barbecue sauce (any brand)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup italian bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. onion powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 medium onion, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup beer</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup milk</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup </span><span style="font-family: book antiqua,palatino;">oil</span><br /><br /></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Blend all ingredients together except oil.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Make meatballs about the width of a quarter.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">You can either fry them on top of the stove or bake them in the oven with the remaining barbecue sauce.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">If you fry them heat them for 5−10 minutes in the oven in the remaining barbecue sauce.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 10:42:36', 62, '', '2010-05-27 14:09:02', 62, 0, '0000-00-00 00:00:00', '2010-04-27 10:42:36', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 62, '', '', 0, 5, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (182, 'Bell Pepper Dip', 'bell-pepper-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 large green bell pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 large red bell pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 large yellow bell pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup mayonnaise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. parmesan cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. paprika</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp oil</span><br /></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Cut and remove the seeds from the peppers. Chop the peppers into small pieces and saute in heated oil for 5−10 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Remove from heat and place in food processor or blender.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add in all ingredients and blend until smooth.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Refrigerate for at least 30 minutes.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 10:55:17', 62, '', '2010-05-27 14:08:40', 62, 0, '0000-00-00 00:00:00', '2010-04-27 10:55:17', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 61, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (183, 'Cancun Stuffed Mushrooms', 'cancun-stuffed-mushrooms', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 lbs. mushroom, cleaned and stemed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup soy sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. spicy sausage</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 green onions, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. oil</span><br /></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Cut sausage into tiny pieces and fry in oil until browned.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In bowl add sausage, garlic powder, soy sauce, onin, salt and pepper. Blend well.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Spoon mixture into mushrooms.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Bake for 20−25 minutes at 350.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 11:00:47', 62, '', '2010-05-27 14:07:59', 62, 0, '0000-00-00 00:00:00', '2010-04-27 11:00:47', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 60, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (184, 'Cannelloni Brushetta', 'cannelloni-brushetta', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 13 oz. cans cannelloni beans, drained and rinsed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 4 oz. jar sweet red pimentos</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 large french bread, cut into 1/2 inch slices<br /></span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In food processor or blender combine beans, olive oil, salt and pimentos. Blend until smooth.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Preheat oven to broil. Place bread on cookie sheet and broil for 1 minute.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Spread bean mixture onto bread and broil for 3−5 minutes. Watching carefully not to burn.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve on nice dish.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 11:09:27', 62, '', '2010-05-27 14:07:11', 62, 0, '0000-00-00 00:00:00', '2010-04-27 11:09:27', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 59, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (185, 'Caper and Garlic Vegetable Dip', 'caper-and-garlic-vegetable-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. capers</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">5 garlic cloves, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup chives</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 egg</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. dijon mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup vegetable oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. pepper\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Food Processor or Blender</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In food processor or blender combine capers, garlic, chives and parsley. Process until chopped fine.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add in the egg, mustard, salt, pepper and lemon juice. For about 5 seconds.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Put the processor on slow blend and and in the olive and vegetable oil.<br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When done it should have the consistency of mayonnaise.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add in the sour cream and blend well again.<br /></span></span> </li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Taste to see if it needs more salt, pepper or lemon juice.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Chill overnight and serve with fresh vegetables.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 11:13:49', 62, '', '2010-05-27 14:06:22', 62, 0, '0000-00-00 00:00:00', '2010-04-27 11:13:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 58, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (186, 'Cheese Ball Spread', 'cheese-ball-spread', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 lb. grated cheddar cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 lb. meunster cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">10 pimento olives, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">pinch of salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. mayonaise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. worcestershire sauce</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In medium bowl add all ingredients aand blend well. You might want to use your bare hands.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mold into ball or even log.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> You can roll in chopped nuts or even paprika.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Refrigerate 2 hours and serve with crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 11:27:45', 62, '', '2010-05-27 14:05:33', 62, 0, '0000-00-00 00:00:00', '2010-04-27 11:27:45', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 57, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (187, 'Cheese Buns', 'cheese-buns', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">12 hot dog buns</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. cheddar cheese, grated</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 stick of butter</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In large bowl combine butter and grated cheese.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Open buns and spread mixture inside.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place on large cookie sheet and broil for 3−5 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Remove from oven and slice bun into 6 pieces and serve.</span></span></li>\r\n</ol>\r\n<p> </p>', '', 1, 1, 0, 20, '2010-04-27 11:33:19', 62, '', '2010-04-27 16:45:34', 62, 0, '0000-00-00 00:00:00', '2010-04-27 11:33:19', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 56, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (199, 'Deep Fried Pork Meatballs', 'deep-fried-pork-meatballs', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ lbs. pork chop meat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ cups red bell pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ cup finely chopped onion</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup chopped garlic</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. black pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 stick butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup flour\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">vegetable oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups bread crumbs</span></span></li>\r\n</ol>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /> </span></p>\r\n<ol>\r\n<li>First in medium fry pan heat butter until melted and add in flour. </li>\r\n<li>Mix well and heat until creamy a dark beige color. </li>\r\n<li>Add in onions and red pepper and heat for 10 minutes and remove from heat.</li>\r\n<li>In large fry cook pork in about ¼ cup oil. Brown well and remove from heat.</li>\r\n<li>In medium bowl combine meat and onion and pepper mixture with hands, be sure to make sure both are cool first. </li>\r\n<li>Let sit in the refrigerator for about 1−2 hours.</li>\r\n<li>When chilled, roll into small balls. </li>\r\n<li>Mix eggs with water and leave in bowl for dipping pork balls. </li>\r\n<li>Place bread crumbs in separate bowl.</li>\r\n<li>First, dip pork balls into egg mixture then coat well with bread crumbs. Do this step to all of the meat before starting to cook.</li>\r\n<li>In large sauce pot, cover bottom of pot with 2 inches of oil and heat on medium to high. The oil has to high enough to submerse the pork ball.</li>\r\n<li>Place as many pork balls in very hot oil and cook for 5−8 minutes, sometimes longer. You will be able to tell. </li>\r\n<li>In small bowl combine mustard and mayonnaise. </li>\r\n<li>To serve place heaping teaspoon in middle of dish and place pork balls around it. </li>\r\n<li>Serve as a first course or appetizer.</li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /></span></p>', 1, 1, 0, 20, '2010-04-28 09:16:50', 62, '', '2010-05-27 13:52:24', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:16:50', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 44, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (188, 'Chicken Tenders with Dipping Sauce', 'chicken-tenders-with-dipping-sauce', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 lbs. chicken tenders</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 eggs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 18 oz. barbecue sauce, any flavor</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat oven to 375 degrees.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine eggs and water. Dip chicken tenders into egg mixture and cover with bread crumbs.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place on baking dish and bake for 50 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In small bowl combine barbecue sauce and sour cream. Chill for 30 minutes before serving.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Arrange on platter and serve or place 3−4 pieces on each plate and top with heaping spoonful of sour cream mixture and serve as appetizer first course.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 12:38:33', 62, '', '2010-05-27 14:03:41', 62, 0, '0000-00-00 00:00:00', '2010-04-27 12:38:33', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 55, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (189, 'Chinese Chicken Wings ', 'chinese-chicken-wings-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 dozen chicken wings</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 cups water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup soy sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup oyster sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup white wine</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix all ingredients together except for chicken wings.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Preheat oven to 350F.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place chicken wings in baking dish and pour over mixture.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake for 30 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve with plenty of napkins.<br /><br /><br /></span><br /><br /></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-27 12:41:17', 62, '', '2010-05-27 14:03:04', 62, 0, '0000-00-00 00:00:00', '2010-04-27 12:41:17', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 54, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (190, 'Corn Salsa ', 'corn-salsa-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cans drained corn</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. lime juise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbsp. olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 large tomatoe, diced and seeded</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 15 oz. can black beans rinsed and drained</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 red onions, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. red wine vinegar</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In bowl combine all ingredients and refrigerate for 2 hours.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add salt and pepper to taste.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with large tortilla chips</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-27 12:44:55', 62, '', '2010-05-27 14:02:29', 62, 0, '0000-00-00 00:00:00', '2010-04-27 12:44:55', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 53, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (191, 'Crab Cakes', 'crab-cakes', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. crabmeat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">5 shallots, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup whipping cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 cups italian bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 white wine</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 scallions, chopped\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup horseradish sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup parsley</span><br /><br /></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In skillet heat 1 tbsp. oil and cook shallots and scallions. Cook until tender.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add in wine,whipping cream and horseradish sauce.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Let simmer for 5 minutes and let cool.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In large bowl combine this mixture with crabmeat, eggs, breadcrumbs and parsley.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Make into either 8 hamburger like patties or 32 small balls.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Heat 1/2 oil until hot and saute the crab cakes or balls in oil. Cook until all sides are golden brown.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> You might want to top with a tsp. of horseradish on each cake or in the center of the 8.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 12:46:53', 62, '', '2010-05-27 14:02:06', 62, 0, '0000-00-00 00:00:00', '2010-04-27 12:46:53', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 52, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (192, 'Crabmeat Stuffed Mushrooms', 'crabmeat-stuffed-mushrooms', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">24 mushrooms, cleaned</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 can (6 oz.) crabmeat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small onion, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. horseradish</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbs.. bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. worcestershire sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbs.. milk</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Combine all ingredients except mushrooms.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Preheat oven to 350 degrees.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Fill mushrooms with crabmeat mixture.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In large baking dish coat with 2 tbs.. of butter and bake mushrooms for 10 minutes.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 12:51:33', 62, '', '2010-05-27 14:01:35', 62, 0, '0000-00-00 00:00:00', '2010-04-27 12:51:33', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 5, 0, 51, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (193, 'Crab Meat Appetizer ', 'crab-meat-appetizer-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 lbs. cooked lump crab meat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 cups mayonnaise, can be light</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbs. dijon mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 red bell pepper, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 green bell pepper, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup chopped scallions</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 egg yolks</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbs. Worcestershire Sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. tobasco sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. salt\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. paprika</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1. Heat oven to 375°.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In large bowl combine all ingredients, mixing well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In medium casserole dish coat side with butter and bread crumbs. (You would use the same technique if you were coating a cake pan with butter than dusting with flour).</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add mixed ingredients to casserole dish and bake for 20−25 minutes. If the top has not browned at the end, turn on broiler for 1 minute.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Let cool for 15 minutes. Serve with crackers or little pieces of torn french bread.<br /></span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-27 13:12:55', 62, '', '2010-05-27 14:00:01', 62, 0, '0000-00-00 00:00:00', '2010-04-27 13:12:55', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 50, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (194, 'Crabmeat Balls', 'crabmeat-balls', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 7 oz. cans crabmeat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">12 slices of bacon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup seasoned bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbs.. cooking sherry</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbs.. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. onion powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">pinch salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. pepper</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Lay bacon on papertowels and cover with papertowels.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place in microwave and cook for 10 minutes on high. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook until totally brown and crispy. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove and let cool.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When cooled, place between papertowels again and crush with hands until you make all crumbs.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-27 13:14:44', 62, '', '2010-05-27 13:56:38', 62, 0, '0000-00-00 00:00:00', '2010-04-27 13:14:44', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 49, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (195, 'Crab Stuffed Mushrooms', 'crab-stuffed-mushrooms', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">2<span style="font-family: book antiqua,palatino;">4 mushrooms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 can crab meat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 egg, beaten with 2 tsp. milk</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small onion, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup parmesan cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. onion powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. parsley</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Preheat oven to 300 degrees.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Clean mushrooms.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine remaining ingredients and stuff into mushrooms.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake for 20 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve.</span><br /></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-27 13:16:53', 62, '', '2010-05-27 13:55:37', 62, 0, '0000-00-00 00:00:00', '2010-04-27 13:16:53', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 48, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (196, 'Cream Cheese and Caviar Dip', 'cream-cheese-and-caviar-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">1<span style="font-family: book antiqua,palatino;"> 12 oz. container whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small jar black caviar</span><span style="font-family: comic sans ms,sans-serif;"><strong><span style="font-family: book antiqua,palatino;"><br /></span><br /></strong></span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix all ingredients well and refrigerate for 1 hour.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve as dip with crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', '', 1, 1, 0, 20, '2010-04-27 13:18:56', 62, '', '2010-04-27 16:42:46', 62, 0, '0000-00-00 00:00:00', '2010-04-27 13:18:56', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 46, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (197, 'Cucumber Dip', 'cucumber-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 large cucumbers</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 8 oz. pkgs. cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup white vinegar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. garlic salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 cup mayonnaise</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Peel cucumber skin off and then grate into medium slices.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix cucumber in vinegar and salt overnight.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In small mixing bowl add cream cheese, mayonnaise and garlic.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Squeeze excess liquid from cucumber and mix cucumbers with cream cheese mixture and stir well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve this with vegetables or tortilla chips.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-27 13:21:03', 62, '', '2010-05-27 13:54:40', 62, 0, '0000-00-00 00:00:00', '2010-04-27 13:21:03', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 47, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (198, 'Curried Yogurt Dip', 'curried-yogurt-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup plain yogurt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. hot sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">pinch of black pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. curry powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. sugar</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine all ingredients and blend well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Refrigerate 20 minutes before serving.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve with vegetables.</span></span></li>\r\n</ol>\r\n<p> </p>', '', 1, 1, 0, 20, '2010-04-27 13:22:37', 62, '', '2010-04-27 16:42:27', 62, 0, '0000-00-00 00:00:00', '2010-04-27 13:22:37', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 45, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (200, 'Deviled Eggs Lisa', 'deviled-eggs-lisa', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span>:</span></p>\r\n<ol>\r\n<li><span style="font-size: small;">1<span style="font-family: book antiqua,palatino;">2 hard boiled eggs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. dijon mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 scallions, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 tbsp. mayonnaise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ tsp. curry powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. dry mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. pepper\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Paprika</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut eggs in half lengthwise and take out yolks and reserve in bowl.<br />2. Mash yolks and mix in all ingredients except Paprika.<br />3. Spoon mixture into eggs and sprinkle with Paprika.<br />4. Serve 3 each for first course or can be used as an appetizer.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 09:29:57', 62, '', '2010-05-27 13:48:20', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:29:57', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 43, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (201, 'Easy Herb Spread', 'easy-herb-spread', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 scallions, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup basil</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Prrocedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Combine all ingredients well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place in pretty bowl and refrigerate for at least 1 hour before serving.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', '', 1, 1, 0, 20, '2010-04-28 09:31:49', 62, '', '2010-05-27 13:47:52', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:31:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 42, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (202, 'English Muffin Crab Cakes', 'english-muffin-crab-cakes', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">8 whole english muffins</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 6 oz. cans crabmeat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. mayonaise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. onion powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small onion, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. pkg. shredded meunster cheese</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Split english muffins and bake on cookie sheet for 10−15 minutes or until turning  slightly brown.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In medium bowl combine remaining ingredients. Blend well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Remove english muffins from oven and place spoonfuls of crabmeat mixture on top of muffins.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Bake for 15 minutes or until cheese is bubbly.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> If you have extra mixture, place in small baking dish and bake for 10 minutes and serve with crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 09:33:20', 62, '', '2010-05-27 13:47:40', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:33:20', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 41, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (203, 'Five Cheese Baked Ziti', 'five-cheese-baked-ziti', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Sauce:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 large cans crushed tomatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 small cans tomato sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 small cans tomato paste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 medium onion, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 garlic cloves, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. onion powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. chili powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. pepper\r\n', '\r\n</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ziti:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 lbs. ziti</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 15 oz. pkgs. of ricotta cheese, skim or whole</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups shredded swiss cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups parmesan cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups shredded provolone</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups crumbled bleu cheese</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Make sauce first. Combine all ingredients and simmer for 1 hour or until ready to use.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In large bowl combine all the remaining ingredients and mix well. Add in half the sauce and mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When ziti is cooked drain very well and add in to sauce and cheese mixture. Mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In 1 or 2 baking dishes pour in ziti mixture and you may want to add some sauce to the top and garnish with sauce and parsley.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake at 350° for 45 minutes. Let set in oven for 15 more minutes and serve</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve with salad and bread.<br /> </span><br /> <br /><br /> .<br /> </span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 09:35:13', 62, '', '2010-05-27 13:46:22', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:35:13', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 40, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (204, 'Fried Meat Ravioli', 'fried-meat-ravioli', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. frozen meat ravioli</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups seasoned bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs, beaten</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 jar premade alfredo sauce</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine eggs and water. Dip ravioli into egg mixture and then into bread crumbs.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat oil in large skillet and fry ravioli until golden brown.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place on papertowel to remove excess grease.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat alfredo sauce on stove or in microwave. Place in bowl to serve.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Arrange ravioli on dish around alfredo sauce.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 09:39:02', 62, '', '2010-05-27 13:45:52', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:39:02', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 39, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (205, 'Fried Mozzarella with Roasted Pepper Sauce', 'fried-mozzarella-with-roasted-pepper-sauce', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 jars roasted peppers</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup red wine vinegar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 16 oz. pkgs. of mozzarella</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups bread crumbs\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ cups vegetable oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs, beaten with ¼ cup of water</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<p><span style="font-size: small;"> <span style="color: #888888;">1.<span style="font-family: book antiqua,palatino;"> Slice mozzarella into slices (approximately 15 per pkg.) Dip mozzarella in egg mixture and then into bread crumbs just<br /> like you would to chicken cutlets. Do all pieces of cheese before starting to fry, it makes it easier.<br /> 2. Heat 1½ cups of oil in large fry pan. Place cheese cutlets in fry pan, do not over crowd. Let brown on both sides watching<br /> carefully not to melt cheese all over the place.<br /> 3. When done set aside on serving platter.<br /> 4. In medium sauce pan heat olive oil, roasted peppers, garlic powder, salt, pepper, water, red wine vinegar and cayenne pepper.<br /> When warmed pour mixture into blender and pulse until creamy.<br /> 5. The sauce can be poured into small ramekins so all your guests have their own sauce and are not dipping into the same dish.<br /></span></span></span></p>', 1, 1, 0, 20, '2010-04-28 09:41:15', 62, '', '2010-05-27 13:45:27', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:41:15', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 38, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (206, 'Fried Tomatoes ', 'fried-tomatoes-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 large red tomatoes, cut into 4 slices each</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 strip of bacon (optional)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 cups mayonnaise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup mustard, any kind</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small onion, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. pepper\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup thyme</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Heat oil in large fry pan.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Make egg wash with the eggs, add a little water.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Dip tomatoes in egg and then in bread crumbs.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place in fry pan and fry until golden brown on both sides.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Set aside on paper towel to remove excess oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> To make sauce, mix together mayonnaise, mustard, onion, salt, pepper, cayenne. parsley and thyme. Mix well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> On salad plate place three tomatoes in an arrangement and place a large tablespoon of mayonnaise in the middle.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 09:46:17', 62, '', '2010-05-27 13:44:58', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:46:17', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 37, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (207, 'Garlic and Blue Cheese Bread', 'garlic-and-blue-cheese-bread', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 large loaf french bread, slice down the middle</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. chunky blue cheese dressing</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. whipped butter, softened</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup parmesan cheese</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Preheat oven to 350.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In small bowl combine blue cheese dressing, garlic, and parmesan cheese.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Spread each loaf of bread with butter.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Spread on blue cheese and parmesan mixture.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake for 15−20 minutes.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /></span></p>', 1, 1, 0, 20, '2010-04-28 09:49:31', 62, '', '2010-05-27 13:44:12', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:49:31', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 36, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (208, 'Garlic Clam Dip ', 'garlic-clam-dip-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">1<span style="font-family: book antiqua,palatino;"> 8 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. worcestershire sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 7 oz. can minced clams, with juice</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Combine all ingredients except clam juice. Mix well. If you want a thinner spread add some clam juice.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve with crackers or vegetables.<br /> </span></span></li>\r\n</ol>', '', 1, 1, 0, 20, '2010-04-28 09:51:11', 62, '', '2010-05-27 13:43:47', 62, 0, '0000-00-00 00:00:00', '2010-04-28 09:51:11', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 35, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (209, 'Sauteed Goat Cheese Cakes', 'sauteed-goat-cheese-cakes', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">1<span style="font-family: book antiqua,palatino;"> cup leeks, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. goat cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup flour</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs, beaten with water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup garlic, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cayenne pepper</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<p><span style="font-size: small;"> <span style="color: #888888;">1. I<span style="font-family: book antiqua,palatino;">n large fry pan saute leek until soft. Empty into medium bowl. \r\n', '\r\nAdd in goat cheese, at room temperature, garlic, salt,<br /> pepper and cayenne pepper. Mix well with hands. Make shapes the size of a flattened egg.<br /> 2. Dip goat cheese shape into flour, egg and bread crumbs. Fry on medium heat until golden brown.<br /> 3. Serve with salad.<br /></span></span></span></p>', 1, 1, 0, 20, '2010-04-28 10:23:47', 62, '', '2010-05-27 13:43:27', 62, 0, '0000-00-00 00:00:00', '2010-04-28 10:23:47', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 34, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (210, 'Hot Stuffed Mushrooms', 'hot-stuffed-mushrooms', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">24 mushrooms, cleaned</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 eggs, beaten</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups crabmeat</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. onion powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. parmesan cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. hot sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Preheat oven to 325.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine all ingredients except mushrooms.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Spoon in mixture to mushrooms and bake for 20 minutes.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 10:45:47', 62, '', '2010-05-27 13:42:59', 62, 0, '0000-00-00 00:00:00', '2010-04-28 10:45:47', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 33, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (211, 'Hot Wings', 'hot-wings', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">5 lbs. chicken wings</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. can tomato sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. black pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tsp. tobasco sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups pancake syrup</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. onion powder</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Bake chicken wings at 400 degrees until thoroughly cooked,depending on size.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix all other ingredients and set aside.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Remove wings from oven and place in sauce mixture.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with a lot of napkins.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 10:47:48', 62, '', '2010-05-27 13:42:28', 62, 0, '0000-00-00 00:00:00', '2010-04-28 10:47:48', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 32, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (212, 'Italian Bread Boat Dip', 'italian-bread-boat-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 Large loaves italian bread</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup onions, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1½ cups crab dip</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup cheddar cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup mayonnaise</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Hollow out one loaf of bread using the pulled out pieces as bite size dipping pieces. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Tear up the whole second loaf and use as dipping pieces.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix together in baking dish onions, crab, cheddar and mayonnaise. Bake for about 10 minutes or until hot.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Pour mixture into hollowed out loaf of bread and serve.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 10:49:48', 62, '', '2010-05-27 13:41:24', 62, 0, '0000-00-00 00:00:00', '2010-04-28 10:49:48', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 31, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (213, 'Jumbo Garlic Shrimp', 'jumbo-garlic-shrimp', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">48 jumbo shrimps, cleaned and deveined</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 tbsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups olive oil</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In large bowl combine oil, cayenne pepper and garlic powder. Blend well and add in the shrimp. Coat the shrimp well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Heat skillet and saute shrimp half at a time.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> When done place in large bowl and use toothpicks or serve 6 on a plate as first course.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /></span></p>', '', 1, 1, 0, 20, '2010-04-28 10:51:47', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 10:51:47', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 30, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (214, 'Little Franks with Orange Dipping Sauce', 'little-franks-with-orange-dipping-sauce', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 pkg. cocktail franks</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cup orange juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup pancake syrup</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In medium sauce pan heat orange juice and pancake syrup. Bring to simmer and add in cocktail franks.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Let simmer for 10 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve in pretty bowl with toothpicks.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 10:53:16', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 10:53:16', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 29, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (215, 'Marinated Mushrooms', 'marinated-mushrooms', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. fresh mushrooms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. chives</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup red wine vinegar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. tarragon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">pinch of salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. garlic powder</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Clean and dry mushrooms.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix all ingredients together.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Let marinate in refrigerator for 8−12 hours.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 10:54:48', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 10:54:48', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 28, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (216, 'Mashed Potato Stuffed Mushrooms', 'mashed-potato-stuffed-mushrooms', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">24 mushrooms, cleaned and stemed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 large baking potatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup finely chopped shrimp</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 stick of butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake potatoes in microwave for 12 minutes. Let cool and scoop out the potato.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In medium bowl combine potato, cream cheese, shrimp, butter and salt Blend well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Spoon mixture into mushrooms and bake for 10−15 minutes at 325.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 10:56:13', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 10:56:13', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 27, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (217, 'Mexican Dip', 'mexican-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lb. ground beef</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. chilli powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 onion, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small chile or jalapeno, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 bag shredded cheddar cheese</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In saute pan brown meat and onion. Add in chilli powder, cayenne pepper and jalapeno.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In bowl combine meat, cream cheese and cheddar cheese.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place in baking dish and bake for 15 minutes.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 10:58:55', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 10:58:55', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 26, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (218, 'Mixed Cheese Spread', 'mixed-cheese-spread', '', '<p><span style="font-size: small;"><strong><span style="font-family: comic sans ms,sans-serif;">Ingredients:</span></strong></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. pkg. of whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 oz. bleu cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cheddar cheese, grated</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. worcestershire sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup cooked, cut up meat (ham or sausage)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 onion, finely chopped</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In large bowl combine all cheese with mixer.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add in all ingredients and refrigerate for 8 hours and serve.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve with small breads or crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:00:27', 62, '', '2010-05-27 13:39:30', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:00:27', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 25, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (219, 'Mozzarella Sticks with Spicy Sauce', 'mozzarella-sticks-with-spicy-sauce', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">40 mozzarella sticks</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. premade plain spaghetti sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 medium onion, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. red pepper flakes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">dash of hot sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. olive oil</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake mozzarella sticks as indicated on pkg.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In medium sauce pan heat onions in oil. Add in remaining ingredients.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat sauce for 30 minutes or length of cooking time for mozzarella sticks.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 11:02:21', 62, '', '2010-05-27 13:39:11', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:02:21', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 24, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (220, 'Mushroom and Nut Pate', 'mushroom-and-nut-pate', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 lbs. mushrooms, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup nuts, any kind</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. vegetable oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. tarragon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. paprika</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup plain yogurt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small onion, finely chopped\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Parsley for garnish, not necessary</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In large skillet heat in oil onion and garlic. Cook until tunring golden brown. Add in garlic  powder, mushrooms. Stir in tarragon, lemon juice, salt, pepper and paprika.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Remove from heat and let stand for 5 minutes. Place in blender and blend for 3 minutes. Add in nuts (almonds) and chop them. Blend for 2 more minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Refrigerate for 8 hours and serve with crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:03:51', 62, '', '2010-05-27 13:38:42', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:03:51', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 23, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (221, 'Mushroom & Sun Dried Tomato Spread', 'mushroom-a-sun-dried-tomato-spread', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ lb. fresh mushrooms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ lb. portabella mushrooms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup chopped sun dried tomatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup green onions, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 garlic cloves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 loaves french bread, cut into diagonal slices</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. olive oil</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In food processor or blender puree all ingredients.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Toast bread in oven until lightly browned. Place bread on serving dish or bowl.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place mushroom mixture into bowl. Spread mixture into bread and serve or have each person do their own.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:05:45', 62, '', '2010-05-27 13:38:07', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:05:45', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 22, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (222, 'Mushroom Bread', 'mushroom-bread', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small loaf white bread</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 lb. mushrooms, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup heavy cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. flour</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 scallion, finely chopped</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Sauter mushrooms in butter. Add in lemon juice, heavy cream, salt, garlic powder, flour and scallions.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Cook until cramy and thick.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Preheat oven to 425.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Remove mushroom mixture from stove.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Dip bread into mixture on both side. Place on cookie sheet and bake on each side for 5−8 minutes.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:07:10', 62, '', '2010-05-27 13:37:41', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:07:10', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 21, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (223, 'Onion Rings with Dipping Sauce', 'onion-rings-with-dipping-sauce', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 large bags of onion rings</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. salsa</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake onion rings as indicated on bag.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In medium bowl combine all ingredients and blend well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Refrigerate for at least 30 minutes before serving.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 11:09:30', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 11:09:30', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 20, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (224, 'Pancake Syrup Chicken Wings', 'pancake-syrup-chicken-wings', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 lbs. chicken wings</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup pancake syrup</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup soy sauce</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Season wings with garlic powder.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake for 30 minutes at 325.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In small bowl combine syrup and soy sauce.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Pour over chicken wings and bake for 10 more minutes.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 11:10:49', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 11:10:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 19, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (225, 'Peanut Chili Dip ', 'peanut-chili-dip-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup creamy peanut butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. chili powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. honey</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. soy sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. garlic powder</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span>:<br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> I<span style="font-family: book antiqua,palatino;">n bowl combine all ingredients until blended well. If mixture seems to thick add more water.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with carrots, broccoli, green and red peppers</span></span></li>\r\n</ol>', '', 1, 1, 0, 20, '2010-04-28 11:12:22', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 11:12:22', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 18, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (226, 'Pesto Crescents', 'pesto-crescents', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. can crescent rolls</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. parmesan cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 egg yolk</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 tsp., sun dried tomatoes in oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbps. pesto sauce ( use envelope)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. water</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Open crescent roll can.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In bowl combine parmesan cheese, sundried tomates and pesto sauce. Mix well.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Open each individual crescent and place 1 tbsp. of mixture into center. Fold over like a triangle. Press edges down firmly with fork.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In small bowl mix together egg and water and with finger paint the egg mixture over the crescent.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Bake for 10−15 minutes or until golden brown.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:14:34', 62, '', '2010-05-27 13:35:38', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:14:34', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 17, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (227, 'Pierogies with Sour Cream', 'pierogies-with-sour-cream', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">32 pierogies, any kind</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 16 oz. sour creams</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. paprika</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small onion</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. olive oil</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In small saute pan heat butter and oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Lay pierogies on large baking dish and pour over oil and butter mixture. Bake for 35 minutes, or until they are turning golden brown.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> You might want to toss once or twice while cooking.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Five minutes before complete sprinkle over the paprika.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve on large plate and place sour cream in bowl in center.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 11:16:24', 62, '', '2010-05-27 13:35:20', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:16:24', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 16, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (228, 'Pork Tenderloin with Roasted Garlic Mayonnaise', 'pork-tenderloin-with-roasted-garlic-mayonnaise', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 12 oz. Pork Tenderlointlets</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">5 tbsp. olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup romano or parmesan cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup parsley</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 Whole Garlic, Medium Head</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup mayonnaise</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;"><span style="color: #888888;"> Preheat oven to 325°. In long mixing dish or pan combine parsley, salt, pepper and cheese.\r\n', '\r\n</span></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">Coat pork tenderloin with oil on all sides and cover with the parsley mixture.</span></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">Place in baking dish with 4 tbsp. of oil and bake uncovered for about 45 minutes to an hour. Also, take the first layer of skin off the garlic bulb and cut off the pointed top.</span></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">Place in small baking dish with 2 tsp. of olive oil and bake for 45 minutes.</span></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">After 45 minutes let garlic cool and squeeze out the garlic like toothpaste. <br /></span></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">With the fork tines, press the garlic down.</span></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">Mix with the mayonnaise well.</span></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">Remove pork from oven and let cool for 10 minutes. Cut into slices and serve two to each plate with a teaspoon of garlic mayonnaise on the side or on top of them.</span></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">Garnish with some parsley.</span></span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 11:17:58', 62, '', '2010-05-27 13:34:19', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:17:58', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 15, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (229, 'Quick Stuffed Celery', 'quick-stuffed-celery', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 bunch of celery, cleaned</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small can black olives, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. paprika</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine cream cheese, sour cream, olives and paprika.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Fill celery stalk and serve.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 11:24:22', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 11:24:22', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 14, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (230, 'Raisin Spread', 'raisin-spread', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup raisins</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cinnamon</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix together all ingredients and refrigerate for 1 hour.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve on bagels or toast.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 11:25:52', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 11:25:52', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 13, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (231, 'Red Bell Pepper Pancakes', 'red-bell-pepper-pancakes', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 cup red bell pepper, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2/3 cup flour</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. melted butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 large eggs, separated</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup cornmeal</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. hot red pepper flakes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. coriander</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 pint sour cream</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">I<span style="font-family: book antiqua,palatino;">n large bowl mix together all ingredients except sour cream and egg whites. Add 1 tsp. salt and ¼ tsp. pepper.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In small bowl beat egg white until fluffy. Fold into flour mixture.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Heat large fry pan with some melted butter. Spoon in balls of the mixture into fry pan. Do not over crowd. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Flatten the balls into small pancakes, about 1−2 inches in diameter. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook each pancake 1 minute on each side or until golden brown.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> These pancakes can be used as appetizers or as first course.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Fo r8 guests, place 5 pancakes on each plate with a large tablespoonful of sour cream. Garnish with parsley.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 11:27:56', 62, '', '2010-05-27 13:31:52', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:27:56', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 12, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (232, 'Salmon Dip', 'salmon-dip', '', '<p><span style="font-size: small;"><strong>Ingredients:</strong></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 can salmon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. whipped cream cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup onions, finely chopped</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Mix all ingredients and refrigerate for 2−4 hours before serving.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"></span></p>', '', 1, 1, 0, 20, '2010-04-28 11:30:21', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-28 11:30:21', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 11, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (233, 'Salsa with Shrimp ', 'salsa-with-shrimp-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">64 jumbo shrimps</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 16 oz. jars of salsa</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. pepper</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> I<span style="font-family: book antiqua,palatino;">n large sauce pan heat the salsa until very hot. Stirring constantly.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add in all the shrimp at once and cook thoroughly for 5 minutes.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place in large serving bowl.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> You can serve this with white rice. Very fast and delicious dish for the salsa lover.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:31:42', 62, '', '2010-05-27 13:31:21', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:31:42', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 10, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (234, 'Sausage Dip', 'sausage-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 lbs. skinny italian sausage, frozen</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup mayonaise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">pinch of salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. paprika</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cayenne pepper</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut sausage into 1 inch pieces. Either fry in frying pan or cook in oven.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix mayonnaise, sour cream, pepper, salt, paprika and cayenne pepper. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Blend well and refrigerate.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Use sauce to dip in sausage. Serve with toothpicks</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 11:33:25', 62, '', '2010-05-27 13:31:02', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:33:25', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 9, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (235, 'Sauteed Mushrooms ', 'sauteed-mushrooms-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 lbs. mushrooms, sliced in half</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 garlic cloves, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. hot red pepper flakes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 stick butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ Sherry</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ cup Teriyaki Sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. parsley</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In large fry pan cook garlic and pepper flakes in butter on low heat.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add in mushrooms, teriyaki sauce and sherry and cook over medium heat until mushrooms are tender.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Remove mushrooms and bring liquid to boil until thickened like syrup.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add in parsley and salt and pepper to taste.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Return mushrooms to heat and serve.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Can serve with any meat.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 11:34:58', 62, '', '2010-05-27 13:30:34', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:34:58', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 8, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (236, 'Sauteed Mushrooms in Biscuits ', 'sauteed-mushrooms-in-biscuits-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 Pkgs. Refrigerated Biscuits, raw</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ lb. shitake mushrooms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ lb. fresh mushrooms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ lb. portabella mushrooms</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups scallions, chopped finely</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cups balsamic vinegar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 cloves garlic, minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ tsp. pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 lemon\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">½ stick of butter</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In large saute pan heat all mushrooms in butter with scallions, garlic, salt and pepper. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When mushrooms cooked and tender place in blender or food processor and pulse until finely chopped.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place back in saute pan. Add in balsamic vinegar and the juice of lemon. heat for 5 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Grease cookie sheet if necessary. Cut biscuits in half. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">To be safe when doing this, place biscuit on counter and cut from right to left or maybe you can even peel them apart.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> When the biscuits are separated, place a tbsp. of mushroom mixture in the middle and place the top of the biscuit back on top. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">To make sure the insides don''t fall out press the whole perimeter of the biscuit.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Bake for 8 minutes on 325°.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:36:31', 62, '', '2010-05-27 13:30:07', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:36:31', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 7, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (237, 'Sesame Chicken', 'sesame-chicken', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">24 chicken tenders</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup sesame seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbsp. dijon mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. onion powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups mayo</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup honey</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure;</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Grease baking sheet. Preheat oven to 450.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Combine dijon mustard, onion powder, mayo and honey. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Dip chicken tenders into this mixture and then into sesame seeds.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place chicken tenders on baking sheet and bake for 20−25 minutes.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:40:06', 62, '', '2010-05-27 13:29:45', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:40:06', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 6, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (238, 'Spinach and Bacon Dip', 'spinach-and-bacon-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup mayonaise</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 pkg. frozen chopped spinach</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">8 slices of bacon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. garlic powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. red pepper flakes</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure;</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">If you can, cook the bacon in microwave until very crispy.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Let cool and break into tiny pieces.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place spinach in strainer − squeeze to remove all excess water.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In medium bowl add all ingredients well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Refrigerate at least 1 hour.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with fruit, vegetables, chips or breads.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 11:41:26', 62, '', '2010-05-27 13:29:25', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:41:26', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 5, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (239, 'Spinach Dip', 'spinach-dip', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 pkg. chopped spinach, cooked and drained</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 pkg. vegetable soup mix</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 8 oz. sour cream</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup mayonaise</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix all ingredients well and refrigerate for 8 hours.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve with crackers.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:42:48', 62, '', '2010-05-27 10:47:31', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:42:48', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 5, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (240, 'Sweet Potato Appetizers', 'sweet-potato-appetizers', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 40 oz. cans sweet potatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 stick of butter</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups pancake syrup</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. pepper</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">In bowl mash potatoes. Add in butter, pancake syrup, salt and pepper.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Make into golf ball size balls. Roll in bread crumbs.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Preheat oven to 350 and bake for 30 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve as an appetizer or with a ham or turkey dinner.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:44:00', 62, '', '2010-05-27 10:46:54', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:44:00', '0000-00-00 00:00:00', '',