INSERT INTO `jos_content` VALUES (241, 'Tomato and Basil Bruschetta', 'tomato-and-basil-bruschetta', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 loaf of french bread, makes 24 pieces</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 garlic cloves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">¼ cup basil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 lbs. seeded tomatoes, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 tbsp. olive oil</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Heat oven to 375°.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Cut bread into 4 inch long pieces. Brush pieces of bread with olive oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Toast in oven for about 10 minutes or until golden brown.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> After toasting, rub garlic on the toast. Mix remaining olive oil and tomatoes and basil in bowl. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add salt &amp; pepper to taste.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Top each piece of toast with about tsp. of mixture.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Place on dish and serve.</span></span></li>\r\n</ol>', 1, 1, 0, 20, '2010-04-28 11:45:22', 62, '', '2010-05-27 10:46:23', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:45:22', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 16, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (242, 'Tomato Potato Skins', 'tomato-potato-skins', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">8 large baking potatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tomatoes, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup shredded mozzarella</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup shredded cheddar\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 green onions, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. olive oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. cayenne pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. onion powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. chili powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 16 oz. sour cream</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Microwave potatoes for 3 minutes each, which should be 24 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut potatoes in half lengthwise. Scoop out potato middles.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In medium bowl combine potato middles with tomatoes, mozzarella, cheddar, onions, olive oil, cayenne, salt, onion powder and chili powder. Blend well.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Spoon mixture back into potato shells and bake for 10−15 minutes.</span></span></li>\r\n</ol>\r\n<p> </p>', 1, 1, 0, 20, '2010-04-28 11:47:08', 62, '', '2010-05-27 10:45:53', 62, 0, '0000-00-00 00:00:00', '2010-04-28 11:47:08', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 7, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (243, 'Coconut Chutney ', 'coconut-chutney-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tbsp. coconut, shredded</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 inch fresh ginger, chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 fresh green chili</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 bunch cilantro with stems and root removed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">fresh lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In a food processor or blender add all ingredients into a pesto like sauce.\r\n', '\r\n</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Coconut Chutney (Thengai Thigayal) - South Indian (fresh)</strong></span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup fresh coconut, shredded</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup Toor dal dry</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup Urad dal dry</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup Channa dal dry</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp.tamarind concentrate</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp. asafetida</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Whole red chilies as per taste upto 3</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. cooking oil</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Dry roast toor dhal,chana dhal,urad dhal,red chilies and asafetida in cooking oil. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Grind this mixture in water into a thick paste. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add coconut, tamarind and salt and grind it for a few more seconds until all the mixture blends into a smooth paste. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve with steamed rice or can be served with dosa (rice pancakes).</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-30 04:05:24', 62, '', '2010-05-27 13:18:51', 62, 0, '0000-00-00 00:00:00', '2010-04-30 04:05:24', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 17, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (244, 'Tamarind Chutney (cooked)', 'tamarind-chutney-cooked', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup cleaned tamarind</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup dates deseeded</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. red chili powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 crushed cumin seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 cup jaggery</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Wash the tamarind clean.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place the tamarind, jaggery, sugar, dates and water in a deep boiling pan.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Soak for a few minutes. Put to boil for about 7-8 minutes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cool to room temperature. Blend in a electric blender till smooth.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Strain and transfer to the pan again. Boil till thick enough to coat the back of a spoon thinly.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the seasoning. Cool again. Store in clean airtight bottles and refrigerate.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"> </span></p>\r\n<p><span style="font-size: small;"> </span></p>', 1, 1, 0, 19, '2010-04-30 04:12:21', 62, '', '2010-05-27 13:18:30', 62, 0, '0000-00-00 00:00:00', '2010-04-30 04:12:21', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 16, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (245, 'Tomato Chutney (cooked) ', 'tomato-chutney-cooked-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 Tbsp. Ghee</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp. red chilies</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cumin seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 inch ginger minced or pounded in a mortar and pestle</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 inch of cinnamon stick</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups coarsely fresh ripe tomatoes</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 Tbsp. jaggery or brown sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat ghee in a large sauce pan over moderate heat.  \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the cumin seeds and let sizzle and brown. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add red chilies, ginger and stir fry for a moment. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add the other ingredients.  <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cook on low for about 20 to 35 minutes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve with meals.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /></span></p>', 1, 1, 0, 19, '2010-04-30 04:15:54', 62, '', '2010-05-27 13:18:07', 62, 0, '0000-00-00 00:00:00', '2010-04-30 04:15:54', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 15, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (246, 'Cashew Nut Chutney ', 'cashew-nut-chutney-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup raw cashews bits or halves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2-inch piece of peeled fresh ginger root,sliced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1-2 hot green chilies, seeded and chopped up to 1/3 cups water</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons chopped fresh coriander</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine the cashews,lemon juice,salt ginger and chilies 1/4 of cup water in a food processor fitted with the metal blade, or a blender, and process until smooth, adding more water as necessary to produce a loose puree. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Transfer to a bowl, add the fresh coriander, and serve or cover well and keep refrigerated for up to 3 days. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Note: This chutney thickens as it sits. Thin it with water to the desired consistency.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-30 06:45:54', 62, '', '2010-05-27 13:17:48', 62, 0, '0000-00-00 00:00:00', '2010-04-30 06:45:54', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 14, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (247, 'Cilantro Chutney ', 'cilantro-chutney-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 bunch cilantro, fresh</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 or 2 small green chili, fresh, remove seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">juice of one lime</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon cumin seeds, roasted, ground</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 pinch of black pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. coriander powder</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Dry roast cumin seeds in a hot cast iron frying pan, until they turn brown.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Grind into powder.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Put all the ingredients into the blender and puree into a paste.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Use as little water as necessary.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /></span></p>', 1, 1, 0, 19, '2010-04-30 06:50:39', 62, '', '2010-05-27 12:42:09', 62, 0, '0000-00-00 00:00:00', '2010-04-30 06:50:39', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 13, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (248, 'Yogurt with Cucumbers', 'yogurt-with-cucumbers', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 green cucumbers</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt and black pepper to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons finely chopped spring onion</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups yogurt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1''/2 teaspoons roasted cumin seeds (optional)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Garnish:</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tablespoon chopped fresh coriander or mint</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel the cucumbers, halve them lengthways and remove the seeds. \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut the cucumbers into small dice, sprinkle with salt and leave for 15 minutes, then drain away liquid and rinse the cucumbers quickly in cold water. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Drain well. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine with onion, yogurt, lemon juice and taste to see if more salt is required. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Roast the cumin seeds in a dry pan, shaking pan or stirring constantly, until brown. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bruise or crush seeds and sprinkle over yogurt. Serve chilled, garnished with mint or coriander.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-30 06:58:31', 62, '', '2010-05-27 12:41:08', 62, 0, '0000-00-00 00:00:00', '2010-04-30 06:58:31', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 12, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (249, 'Palak Pachchadi ', 'palak-pachchadi-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 large bunch spinach 2 teaspoons ghee or oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon black mustard seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">l teaspoon cumin seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon ground cumin</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon fenugreek seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/8 teaspoon chili powder (optional)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Salt and black pepper to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups yogurt</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Wash spinach thoroughly in several changes of water.  \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove any tough stems and put the leaves into a saucepan with very little water. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cover and steam over low heat until spinach is tender. Drain and chop finely.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In a small pan heat ghee or oil and fry the mustard seeds until they start to pop. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add cumin seeds, ground cumin and fenugreek seeds and continue to fry, stirring with a wooden spoon, until the fenugreek seeds are golden brown, but do not allow to burn. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Remove from heat, stir in chili powder, if used, and salt and allow to cool. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix in the yogurt, then stir this mixture into the spinach. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve cold or at room temperature as a side dish with rice and curry. or with one of the Indian breads.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-30 07:02:38', 62, '', '2010-05-27 12:40:33', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:02:38', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 11, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (250, 'Yogurt and Onion Salad', 'yogurt-and-onion-salad', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">3 medium onions</span></li>\r\n<li><span style="font-size: small;">Salt and black pepper to taste</span></li>\r\n<li><span style="font-size: small;">1 cup yogurt</span></li>\r\n<li><span style="font-size: small;">1 teaspoon finely grated fresh ginger</span></li>\r\n<li><span style="font-size: small;">2 medium tomatoes, peeled and chopped</span></li>\r\n<li><span style="font-size: small;">3 fresh green chilies, seeded and chopped</span></li>\r\n<li><span style="font-size: small;">3 tablespoons chopped fresh cilantro or coriander</span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cut the onions into thin slices, sprinkle with the salt and set aside for 20 minutes. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Squeeze out as much liquid as possible.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix together the yogurt and ginger, then fold in the onions and the rest of the ingredients. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cover and chill thoroughly before servin</span>g.</span></li>\r\n</ol>\r\n<p><span style="font-size: small;"> </span></p>', 1, 1, 0, 19, '2010-04-30 07:05:54', 62, '', '2010-05-27 12:38:33', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:05:54', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 10, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (251, 'Yogurt with Bananas ', 'yogurt-with-bananas-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 large ripe bananas lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon cumin seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup yogurt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tablespoons freshly grated or desiccated coconut</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons sugar</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Slice the bananas and sprinkle with lemon juice.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Roast cumin seeds in a dry pan, shaking or stirring constantly until brown.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Crush or grind. Combine the yogurt with all the ingredients except the banana.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> If desiccated coconut is used, moisten it first by sprinkling with about 2 tablespoons water and tossing it with the fingers until it is no longer dry.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Fold banana into yogurt mixture. Chill and serve.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /></span></p>', 1, 1, 0, 19, '2010-04-30 07:08:40', 62, '', '2010-05-27 12:38:11', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:08:40', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 9, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (252, 'Salad South Indian Style', 'salad-south-indian-style', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 carrot, peeled and julienne</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cucumber, peeled and diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tomato, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 green chili (chili pepper), minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 small bunch of cilantro, coriander leaves minced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">salt and pepper to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons lemon/lime juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">For Salad Dressing</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon brown mustard seeds \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon black gram dal (washed urad dal), picked over and rinsed</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 red chili (chili pepper), halved</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon asafetida powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">a few curry leaves (optional)</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the first 7 ingredients in a bowl and mix thoroughly. Set aside.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Making the salad dressing: <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat 2 teaspoons oil in a heavy pan or skillet. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add the mustard seeds, black gram dal, halved red chili, asafetida, and a few curry leaves.  <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">When the mustard seeds splutter, add this mixture to the vegetables. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Now add the lemon juice, and mix thoroughly. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve at room temperature.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-30 07:12:08', 62, '', '2010-05-27 12:37:52', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:12:08', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 8, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (253, 'Salad North Indian Style ', 'salad-north-indian-style-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;">2<span style="font-family: book antiqua,palatino;"> to 3 large Tomatoes, diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 large Cucumber, peeled and finely diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4th cup Onion, finely diced</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 Green chili, finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A few sprigs of Coriander / Cilantro leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 to 2 teaspoons Sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4th teaspoon Salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Lime juice to taste</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 tsp. cumin seeds that are dry roasted in a skillet and then ground in a mortar</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Add all the ingredients in a bowl and mix thoroughly. Set aside.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Serve at room temperature.</span></span></li>\r\n</ol>', '', 1, 1, 0, 19, '2010-04-30 07:15:34', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-30 07:15:34', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 7, '', '', 0, 9, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (254, 'Fiery Fruit Salad', 'fiery-fruit-salad', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 lbs. of mixed fruit in season peeled, de-seeded and cut in 1 cm. cubes (banana and oranges are a must. Apple, pear, nectarine, plums, guava, mango, pitted       cherries, pineapple, kiwi, seedless grapes.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4 tbsp. fresh lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. cayenne</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. cumin seeds, roasted and crushed into a powder</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. paprika</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. black pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2  tsp. mint</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tsp. powdered ginger (optional)\r\n', '\r\n</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Place all fruit in a large serving bowl and mix in lemon juice. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Mix all the spices in a separate bowl, pour over the fruit and mix well with your hands. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Cover the bowl with plastic wrap and refrigerate until well chilled, at least 1 hour. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Makes 6 servings</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"> </span></p>', 1, 1, 0, 19, '2010-04-30 07:19:34', 62, '', '2010-05-27 12:36:56', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:19:34', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 6, '', '', 0, 6, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (255, 'Mango/Pineapple Fruit Salad', 'mangopineapple-fruit-salad', '', '<p><span style="font-family: book antiqua,palatino;"><strong>Ingredients:</strong><br /></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">4  cups mixed fruits chopped (pineapple, mango, apple and green or black grapes depending on availability)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">4  tablespoons fresh grated coconut</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">2  dry red chilli(es)</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1  teaspoon(s) mustard seeds</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">2  tablespoon(s) sugar or as per taste</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">salt to taste</span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><strong>Procedure:</strong></span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">Mix the sugar into the chopped fruits and keep aside.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Grind the coconut with the red chilli(es) and a little water. \r\n', '\r\n</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">When almost done, add the mustard seeds. <br /></span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Grind for a few more seconds till the mustard seeds are crushed. <br /></span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Add the coconut paste to the fruits with salt and mix well.</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Serve cold or at room temperature.</span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"> </span></p>', 1, 1, 0, 19, '2010-04-30 07:27:40', 62, '', '2010-05-27 12:36:17', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:27:40', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 5, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (256, 'Sprouted Moong dal Salad ', 'sprouted-moong-dal-salad-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2  cups sprouted green gram or moong dal</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">4  tbsp. onions finely chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2  cups tomato and cucumber chopped</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2  big lettuce leaves sliced fine</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2  tbsp. roasted peanuts</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">A dash of lemon and salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Gently mix all the ingredients except for the roasted peanuts in a salad bowl. Keep refrigerated.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix in the roasted peanuts just before serving.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve cold.</span></span></li>\r\n</ol>', 1, 1, 0, 19, '2010-04-30 07:36:51', 62, '', '2010-05-27 12:35:55', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:36:51', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 4, '', '', 0, 5, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (257, 'Salad Gujarati ', 'salad-gujarati-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">5 carrots, trimmed, peeled, and coarsely grated</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 tsp. salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tbsp. vegetable oil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. whole black mustard seeds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tsp. lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tablespoon fresh grated coconut or coconut flakes</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Toss grated carrots with salt. <br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Heat oil over medium flame until very hot, add mustard seeds.\r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> When they begin to pop, pour contents of pan-oil and seeds-over carrots.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Add lemon juice and toss. Add the grated coconut.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix thoroughly. Serve at room temperature.</span></span></li>\r\n</ol>', 1, 1, 0, 1, '2010-04-30 07:41:45', 62, '', '2010-05-27 12:35:28', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:41:45', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 5, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (258, 'Benefits of fruits and Vegetables', 'benefits-of-fruits-and-vegetables', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Benefits of Fruits and Vegetables</strong></span></span></p>\r\n<ol>\r\n<li> <span style="font-size: small;"><span style="font-family: book antiqua,palatino;">* <strong>Blueberries</strong> are known for their very high antioxidant content and anti-aging properties. In recent experiments with aged laboratory animals, concord grape juice also improved short-term memory and neuromotor skills. Concord grape juice, a refreshing source of concentrated antioxidants, may also help retard mental and physical declines of aging.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> * In an analysis of the antioxidant capacity of vegetables, fruits, and vegetable oils, it was found that <strong>spinach, peppers, asparagus and turnip </strong>tops had the highest antioxidant capacity among the vegetables; blackberries, raspberries, strawberries and olives ranked highest among the fruits; while soy oil and extra virgin olive oil had the highest antioxidant capacity among the oils tested.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> * Risk of breast cancer among postmenopausal Japanese women was reduced about<strong> 40 percent for an increased intake of fruit.</strong></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> * Cranberries, which are native to North America, are very popular during the year-end holiday season. Their high fiber content is useful in forming gels for traditional sauces. <strong>Cranberries</strong> are very high in antioxidants such as proanthocyanidins, which provide protection against cancer and heart disease. These compounds are also useful for fighting urinary tract infections.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> * Middle-aged Finnish men with the highest consumption of fruits, berries and vegetables experienced <strong>a 41 percent reduction in cardiovascular disease</strong>-related death and a <strong>34 percent reduction in death from all causes, compared to men with the lowest intake.</strong></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> * <strong>Fruits and vegetables</strong> are known to protect against chronic disease because of their rich content of many phytochemicals, including the antioxidants. Cranberry was found to possess the highest antioxidant activity, followed by apple, red grape, strawberry, peach, lemon, pear, banana, orange, grapefruit, and pineapple.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> * Iceberg lettuce, tomatoes, and French fried potatoes are the most commonly consumed vegetables in the U.S. However, only 3% of those surveyed consumed broccoli during the reporting period while consumption of dark, green vegetables averaged only one serving every 5 days.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> * Health authorities recommend that . Less than 10 percent of Americans regularly consume these foods.</span><span style="font-family: book antiqua,palatino;"><strong>one citrus fruit and one  cruciferous vegetable be eaten daily</strong></span></span></li>\r\n</ol>', '', 1, 10, 0, 11, '2010-04-30 07:51:39', 62, '', '2010-04-30 08:21:22', 62, 0, '0000-00-00 00:00:00', '2010-04-30 07:51:39', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 4, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (259, 'Black Grapes', 'black-grapes', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Black Grapes:</strong></span></span></p>\r\n<p><span style="font-size: small;">•    <span style="font-family: book antiqua,palatino;">Grapes are native to the Mediterranean region and Central Asia. They grow in clusters on a deciduous woody vine, Vitis vinifera, and come in many colors. Blue, red, purple, black, golden, and green are the most common. The original grapes were red, and the dark grapes all contain a rich supply of anthocyanins, the antioxidant polyphenolic that conveys many health-promoting properties of grapes. Grapes rank with blueberries and blackberries as excellent sources of antioxidants. </span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    Grapes and grape juice function in different ways in the cardiovascular system. The pigments in red, purple and black grapes protect the cardiovascular system by a number of mechanisms that include the suppression of blood clots, inhibition of LDL cholesterol oxidation, reduced homocysteine levels, and anti-atherosclerotic properties. Grapes stimulate endothelial nitric oxide production which induces relaxation of the blood vessel walls, and a reduction in blood pressure levels. Recently, it was shown that a Concord grape extract lowered LDL cholesterol, raised HDL cholesterol, and decreased plasma inflammatory biomarkers. </span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    Promising data suggests the use of grape juice to promote brain health and delay neurodegenerative diseases. Participants that drank purple grape juice and similar fruit juices three times a week were about 70% less likely to develop Alzheimer''s disease. When laboratory animals were fed Concord grape juice they showed significantly improved scores on memory and coordination tests. <br /> •    Health benefits of green grapes include reduction of inflammation that happens during COPD. </span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    Grapes also prevent macular degeneration.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    The gorgeous, luscious beads of grapes that we consume comprise 70-80% water and 15-30% sugar in the form of glucose or fructose. Besides these two main constituents, grapes also contain various essential nutrients like magnesium, calcium, manganese, gallic acid, phosphoric acid, salicylic acid, oxalic acid and loads of vitamins like A, B complex, C and K. 100 grams of grapes contains 60 calories.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    <strong>Reduces Accumulation of Oxidized Cholestero</strong>l: Of the different grape varieties, red grapes health benefits are many, especially in the area of heart health. Plaque accumulation on the walls of the arteries cause blockage of blood flow to the heart. This in turn results in strokes or cardiac diseases. Fresh grapes contain antioxidants called polyphenols, which prevent accumulation of oxidized cholesterol on the walls of the arteries. One polyphenol called resveratrol is known for protecting the arterial walls from damage. Then there''s the other nutrient pterostilbene which reduces cholesterol and triglycerides by acting on the enzymes controlling regulation of fat content in the blood. Read more on red grapes nutrition facts.</span></span></p>\r\n<p><span style="font-size: small;"><br /> •   <span style="font-family: book antiqua,palatino;"> <strong>Decreases Platelet Aggregation:</strong> Various studies conducted on grapes have revealed that flavanoids present in grapes reduce blood clotting. These flavanoids speed up the release of nitric oxide, thereby increasing levels of nitric oxide in the blood. These increased levels of nitric oxide in the blood prevent the blood platelets from aggregating, thereby prevent blood clots. Thus, consumption of grapes reduces the risk of heart attacks.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •   <strong> Lowers Risks of Alzheimer''s Disease:</strong> The polyphenol, resveratrol is also known to reduce the levels of amyloid-beta peptides in people suffering from Alzheimer''s disease. Grapes are known to stall the onset of various neurodegenerative diseases and also promotes brain heath. Studies have shown that consumption of red wine on a moderate basis has resulted in lower incidence of Alzheimer''s disease.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    <strong>Promotes Lung Health:</strong> Some studies reveal that consuming red wine helps reduce risks of lung cancer. Red wine contains large amounts of resveratrol which has lots of anti-inflammatory effects on the epithelial cells lining the lungs, trachea, etc. Grapes also increase the moisture content in the lungs and can also reduce asthma problems.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •   <strong> Reduces Cancer Risk:</strong> Grapes comprise two groups of polyphenols called anthocyanins and proanthocyanidins that are capable of detoxifying the activities of various cancer spearheading substances. Grape juice made from concord grapes help prevent DNA damage of breast cells by a cancer causing chemical: carcinogen. Moreover, grapes also contain high levels of caffeic acid, which is an effective cancer countering substance. The bioflavanoids present in the grapes help strengthen the body''s immune system to destroy cancer cells. Resveratrol prevents liver, breast, lung and prostate cancer.</span></span></p>', '', 1, 10, 0, 11, '2010-04-30 08:03:57', 62, '', '2010-04-30 08:21:09', 62, 0, '0000-00-00 00:00:00', '2010-04-30 08:03:57', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (260, 'WaterMelon', 'watermelon', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>WaterMelon:</strong></span></span></p>\r\n<p><span style="font-size: small;">•    <span style="font-family: book antiqua,palatino;">Watermelon contains a good amount of water, which makes it very low in calories. This fruit is rich in vitamin A and vitamin C. Small amounts of vitamin B1, vitamin B2, vitamin B6, folate, niacin, etc. are also found in watermelon. It is very rich in potassium and also contains minerals like iron, calcium, magnesium, and phosphorous in traces. </span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    Watermelon is also free of fats and cholesterol. Apart from the vitamins and minerals, there are many components in this fruit which are also responsible for the health benefits of watermelon. </span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    It is rich in antioxidants called carotenoids, like lycopene, phytofluene, phytoene, beta-carotene, lutein, and neurosporene. Watermelon seeds contain a good quantity of protein and oil. Around 35% of watermelon seed is protein and 50% is oil, along with 5% of dietary fiber. These seeds also contain minerals like magnesium, calcium, potassium, iron, phosphorous, zinc, etc. </span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    <strong>Prevents Heart Attack and Stroke:</strong> Watermelon are rich in antioxidants like vitamin C, vitamin A (mainly in the form of beta carotene) and carotenoid lycopene. Antioxidants neutralize the free radicals in the body. Free radicals are the primary causes of heart attacks and strokes. Vitamin C and beta carotene fight free radicals and reduce the risk of diseases, like, heart attack, stroke, etc. Antioxidants also reduce cholesterol, which apart from causing heart related problems, can worsen other diseases too. Read more on heart attacks.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    <strong>Reduces the Risk of Cancer:</strong> Watermelon is rich is antioxidants, called carotenoids, especially lycopene. Studies show that lycopene is beneficial in preventing some forms of cancer, like, breast cancer, prostrate cancer, endometrial cancer, lung cancer and colorectal cancers. Lycopene is an antioxidant, which can protect the genetic material of white blood cells and also prevents oxygen damage in cells and other structures, which has been found to be linked to the occurrence of cancer in humans.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    <strong>Boosts Energy Production:</strong> We all know that B vitamins are essential for energy production in the body. Watermelon, being rich in B vitamins, can provide you with important B vitamins like Vitamin B1, vitamin B2 and vitamin B6. Apart from that it prevents dehydration, due to the high water content. Read more on vitamin B.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    <strong>Carotenoids:</strong> Watermelon is a concentrated source of carotenoids; lycopene, lutein, phytofluene, phytoene, beta-carotene and neurosporene, with lycopene present in maximum quantity. Carotenoids are antioxidants in nature, which is essential for controlling free radicals. Unchecked free radicals can cause diseases such as cancer, and increases the risks of various cardiovascular diseases.</span></span></p>\r\n<p><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /> •    <strong>Bone Health: </strong>As watermelon is high on calcium, it helps build up strong bones. Watermelon also contains vitamin C and beta-carotene, which helps in the production of collagen, a substance that holds together muscles and bones. Together, these nutrients not only help alleviate the symptoms of osteoarthritis and rheumatoid arthritis, but prevent its occurrence too.</span></span></p>', '', 1, 10, 0, 11, '2010-04-30 08:10:53', 62, '', '2010-04-30 08:20:40', 62, 0, '0000-00-00 00:00:00', '2010-04-30 08:10:53', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (261, 'Strawberry', 'strawberry', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Strawberry:</strong></span></span></p>\r\n<p><span style="font-size: small;"><br /><span style="font-family: book antiqua,palatino;"><span style="color: #888888;">Strawberry is one of the most delicious and nutritious fruit. It has the ability to remove harmful toxin in the blood. <br />It is also recommended for sluggish liver, gout, rheumatism, constipation, high blood pressure, catarrh and even skin cancer.<br />Strawberry is a rich source of folic acid, folic acid helps to prevent / reduce birth defects involving brain.<br /><strong>Nutritive Value </strong>: Per 100 gm.<br /> * Vitamin A : 60 I.U.<br /> * Vitamin B : Thiamine .03 mg.;<br /> * Vitamin C : 60 mg.<br /> * Calcium : 28 mg.<br /> * Phosphorus : 27 mg.<br /> * Potassium : 220 mg.<br /> * Carbohydrates : 8.3 gm<br /> * Calories : 37</span><br /><span style="color: #888888;"><strong>Health Benefits of Strawberry :</strong></span></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">This is highly rated as skin cleansing food, even though the skin eruptions may appear at first in some cases.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> It is also known to clean or rid the blood of harmful toxins.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> It has been reported to help cases of syphilis</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> The strawberry, when cut in half and rubbed on the teeth and gums, removes tartar from the teeth and strengthens and heals the gums. The juice should be allowed to remain on the teeth as long as possible in order to dissolve the tartar. The mouth may then be rinsed with warm water.</span></span></li>\r\n</ol>', '', 1, 10, 0, 11, '2010-04-30 08:17:55', 62, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-04-30 08:17:55', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 1, '', '', 0, 1, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (262, 'Matri', 'matri', '', '<p><span style="font-size: small;">\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong><span style="color: #262626;"><span style="font-weight: bold; color: #262626;">Ingredients:</span></span></strong></span></p>\r\n</span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">1 cup               plain flour </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 tbsp.              ghee or oil </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">salt to               taste </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">2-3               pinches soda bicarb </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/4 tsp.              ajwain seeds </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">warm               water for kneading </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">oil for               deep frying</span> </li>\r\n</ol>\r\n<p><span style="font-size: small;">\r\n<p><span style="font-family: comic sans ms,sans-serif;"><strong><span style="color: #262626;"><span style="font-weight: bold; color: #262626;">Procedure:</span></span></strong></span></p>\r\n</span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">Mix all              ingredients, except oil for deep frying and water. \r\n', '\r\n</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Cover               and keep for 15 minutes. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Break into small portions,  roll in 1"              wide rounds. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Keep a little thicker than puris. Prick  with a              fork. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Place on a clean spread out cloth. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Finish  for all              dough. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Heat oil in a frying pan, add some matri at a  time. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Fry till lightly golden, flip side and repeat. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Drain               and cool to allow to become crisp. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Store in an  airtight              container.</span> </li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><span style="font-size: small;">\r\n<p>Makes: 25 pieces (approx.) Shelf life: 2  weeks at              room temperature.</p>\r\n</span></span></p>', 1, 1, 0, 1, '2010-04-30 08:30:49', 62, '', '2010-05-27 12:22:18', 62, 0, '0000-00-00 00:00:00', '2010-04-30 08:30:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 5, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (263, 'Khaman', 'khaman', '', '<p><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong>:</span></p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">1 cup              rice </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 cup   urad dal </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1 cup  yellow moong dal </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">3  cups  sour               buttermilk </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">2 green  chillies crushed fine </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/4  tsp.  ginger               grated fine </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2  tsp. soda bicarb</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">2 tbsp.  oil </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">2-3    pinches              red chilli powder </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">1/2  tbsp. coriander finely  chopped  \r\n', '\r\n</span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Salt   to              taste<br /></span></li>\r\n</ol>\r\n<p><span style="font-family: book antiqua,palatino;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure</strong></span>:</span></p>\r\n<p> </p>\r\n<ol>\r\n<li><span style="font-family: book antiqua,palatino;">Mix the              rice and dal, wash, drain and dry on a clean cloth for a few  hours. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">The grain should be completely. Grind to a coarse  flour in dry              grinder or at the flour mill. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">This flour should be  like very              fine soji in texture. Store in airtight container and use as               required. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Will keep good upto 2 months. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">To make  khaman,              take 1 cup flour in a bowl. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Add buttermilk, and mix  well.              Keep- aside for 4-5 hours. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Dissolve sodabicarb in the  oil. Add              to batter. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Mix all ingredients except red chilli  powder and              coriander. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Pour immediately in a 6" diam. greased  plate. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Steam over water either in a cooker or steamer. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Pierce               knife, and check, should come out clean if done. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Sprinkle  the              chilli powder and coriander, steam again for 2-3 minutes. </span></li>\r\n<li><span style="font-family: book antiqua,palatino;">Cut               into squares or diamonds and serve hot with coconut chutney. </span></li>\r\n</ol>\r\n<p> </p>\r\n<p><span style="font-family: book antiqua,palatino;"><br />Making time:15 minutes(excluding flour grinding and              fermenting time) <br />Makes: 12-14 pieces </span></p>\r\n<p> </p>', 1, 1, 0, 1, '2010-04-30 08:38:26', 62, '', '2010-05-27 12:25:15', 62, 0, '0000-00-00 00:00:00', '2010-04-30 08:38:26', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 4, '', '', 0, 9, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (264, 'Thepla', 'thepla', '', '<p><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients</strong>:</span></p>\r\n<ol>\r\n<li>1 cup              wheat flour </li>\r\n<li>1/4 cup  gramflour </li>\r\n<li>1 tsp.  red chilli  powder </li>\r\n<li>1/4 tsp.  turmeric powder </li>\r\n<li>salt to  taste </li>\r\n<li>4-5   pinches              asafoetida </li>\r\n<li>1/2 tsp.  cumin seeds </li>\r\n<li>1/2 tsp.  sesame  seeds </li>\r\n<li>2               tbsp. oil </li>\r\n<li>oil for  shallow frying\r\n', '\r\n</li>\r\n</ol>\r\n<p> </p>\r\n<p><span style="font-size: small;"><strong><span style="font-family: comic sans ms,sans-serif;">Procedure</span></strong></span>:</p>\r\n<p> </p>\r\n<ol>\r\n<li>Mix all              ingredients except oil for shallow frying. </li>\r\n<li>Use a  little water              and knead a soft pliable dough . </li>\r\n<li>Divide dough into  9-10 balls,              roll each into a 5" round. </li>\r\n<li>Sprinkle flour over  chappati while              rolling, for ease. </li>\r\n<li>Place on a hot griddle, roast and  drizzle              oil, to shallow fry. </li>\r\n<li>Repeat for other side. Repeat for               remaining dough. </li>\r\n<li>Serve hot with chunda (refer pickles)  or              other pickle or curds. </li>\r\n<li>Equally tasty when cold too. </li>\r\n</ol>\r\n<p> </p>\r\n<p><br />Making time: 30 minutes <br />Makes: 9-10 theplas</p>\r\n<p> </p>', 1, 1, 0, 1, '2010-04-30 08:45:51', 62, '', '2010-05-27 12:10:59', 62, 0, '0000-00-00 00:00:00', '2010-04-30 08:45:51', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 3, '', '', 0, 4, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (269, 'ISKCON Temple', 'iskcon-temple', '', '<p>ISKCON temple in Bangalore is a famous temple devoted to Krishna  attracting thousands of devotees and tourists everyday to this modern  spiritual spot</p>', '', 1, 4, 0, 6, '2010-05-28 17:04:50', 62, '', '2010-06-22 11:49:13', 62, 0, '0000-00-00 00:00:00', '2010-05-28 17:04:50', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (265, 'Salt And Pepper Parathas ', 'salt-and-pepper-parathas-', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups wheat flour </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Salt to taste </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Oil or ghee to shallowfry</span></span> </li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Filling: </strong></span><br /></span></p>\r\n<ol>\r\n<li> <span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 tbsp. wheat flour </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tbsp. coriander leaves finely chopped </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1 tsp. mint leaves finely chopped </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1/2 tsp. pepper powder </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1/2 tsp. jaljira or amchoor (dried mango powder) </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> 1/2 tsp. cumin seed powder  \r\n', '\r\n</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Salt to taste</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Add salt and knead flour to a smooth pliable dough. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Keep aside for ten minutes at least. Refrigerate if using much later. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Mix all ingredients for filling, except oil. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Divide dough in 7-8 portions. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Shape lumps into smooth balls of dough. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Using dry flour for dusting, roll into a thick chappati, 5" diameter. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Drizzle some fat and spread evenly on round. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Roughly divide filling into the number of rounds you will have. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Sprinkle one portion over the spread and greased chappati. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Starting from one end, roll tightly, to form a cylinder. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> For a coil of this, press firmly, and roll out to a round of 6" diameter. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Heat a griddle, shallow fry paratha on both sides till golden and crisp. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Serve hot with chunda, chutney or sauce. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Time saver: Prepare and refrigerate dough for 5-6 hours. Dry filling can also be refrigerated in a closed Container and used next day. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Making time: 30 minutes , Makes: 7-8 parathas , Shelflife: 2-3 days</span></span></li>\r\n</ol>\r\n<p> </p>\r\n<p><span style="font-size: small;"><br /></span></p>', 1, 1, 0, 1, '2010-04-30 08:55:16', 62, '', '2010-05-27 14:26:37', 62, 0, '0000-00-00 00:00:00', '2010-04-30 08:55:16', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 1, '', '', 0, 35, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (266, 'Apple Pie', 'apple-pie', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /><strong>Crust:</strong><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/3 cup rolled oats</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">6 graham crackers</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons chopped walnuts</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 egg white</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Buttered flavored vegetable cooking spray</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><strong>Filling:</strong><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups apple cider</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons fresh lemon juice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons vanilla extract</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 1/4 teaspoon pumpkin pie spice</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">5−6 large Granny Smith apples, about 2 1/2 pounds</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3 tablespoons cornstarch</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><strong>Topping:</strong><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/3 cup brown sugar</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/3 cup rolled oats</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons chopped walnuts</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon ground cinnamon</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons butter, melted</span></span></li>\r\n</ol><ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Set oven temperature to 350 degrees.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In a food processor, process  the oats, graham crackers and walnuts<br /> until finely ground. Add the egg white and process until blended.<br /> The mixture should look crumbly not gooey.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Coat a 9−inch pie pan with  cooking spray. Press the crumb mixture<br /> evenly into the pie pan to make the crust. Lightly coat with cooking<br /> spray. Bake at 350 degrees for 10 minutes. Remove from oven and set<br /> aside until ready to use.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bring the cider to a boil in  a large heavy saucepan over high heat.<br /> Cook until reduced to 1/2 cup, about 15 minutes. Pour the mixture<br /> into a large mixing bowl. Add the sugar, lemon juice, vanilla, and<br /> pumpkin pie spice to the bowl.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Peel the apples, cut into  quarters,<br /> remove the core and cut into thin slices. Stir the apples into the<br /> cider mixture. Sprinkle the cornstarch over the apples and stir to<br /> combine. Arrange the apples in the crust.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">In a small bowl, mix the  brown sugar, oats, walnuts and cinnamon<br />together. Pour the melted  butter into the bowl stirring to combine</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Sprinkle the topping over  the apples, pat down with your hands and<br />bake at 350F for 1 hour to 1  hour and 15 minutes. The center should<br />be soft when pierced with a  sharp knife.</span><br /><span style="font-family: book antiqua,palatino;"><br /><br /> <br /><br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"><br /></span></span></li>\r\n</ol>', '', 1, 1, 0, 19, '2010-04-30 10:18:35', 62, '', '2010-05-27 14:25:12', 62, 0, '0000-00-00 00:00:00', '2010-04-30 10:18:35', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 3, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (267, 'Baked Chicken Breast with Fresh Basil', 'baked-chicken-breast-with-fresh-basil', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">10 boneless skinless chicken breast (about 2 1/2 lbs.)</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">3/4 cup low−fat yogurt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 chopped fresh basil</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons cornstarch</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 cup bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 tablespoons grated Parmesan cheese</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"> <span style="font-family: book antiqua,palatino;">Arrange chicken in single layer in baking dish.<br /></span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine yogurt,basil and cornstarch; mix well and spread over chicken.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Combine bread crumbs with Parmesan and sprinkle over chicken.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">If making in advance, cover and refrigerate for up to 6 hours.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake chicken in 375 degrees oven for 30 minutes or until chicken is no longer pink inside</span></span></li>\r\n</ol>', '', 1, 1, 0, 19, '2010-04-30 10:28:34', 62, '', '2010-05-27 14:24:57', 62, 0, '0000-00-00 00:00:00', '2010-04-30 10:28:34', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 2, '', '', 0, 3, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES (268, 'Baked Macaroni, Cheese & Spinach', 'baked-macaroni-cheese-a-spinach', '', '<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Ingredients:</strong></span><br /></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 pound elbow macaroni</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">15 oz. low fat ricotta cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 3/4 cups skim milk</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 cup egg substitute</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 teaspoons brown mustard</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 −2 teaspoons Tabasco sauce</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/2 teaspoon salt</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 teaspoon ground pepper</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups shredded low fat cheddar cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">2 cups torn fresh spinach leaves</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup grated Parmesan cheese</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1/4 cup fine bread crumbs</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">1 teaspoon paprika</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><strong>Procedure:</strong></span></span></p>\r\n<ol>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Preheat oven to 375F. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Spray a 9 X 13 or 3 quart baking dish with nonstick spray oil.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Cook macaroni according to package directions.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Meanwhile, in a food processor or blender, mix ricotta cheese,milk, egg substitute, mustard, Tabasco sauce, salt and pepper.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Process until smooth. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Drain macaroni, return to pot and add shredded cheddar and spinach.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> Stir until cheese melts and spinach wilts.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Stir in ricotta mixture and scrape into prepared baking dish.</span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;"> In a small bowl, combine Parmesan cheese, bread crumbs,and paprika. Sprinkle evenly over macaroni and cheese. </span></span></li>\r\n<li><span style="font-size: small;"><span style="font-family: book antiqua,palatino;">Bake at 375F for 25 minutes. Serve hot.</span></span></li>\r\n</ol>\r\n<p><span style="font-size: small;"><br /><br /><br /></span></p>', '', 1, 1, 0, 19, '2010-04-30 10:41:03', 62, '', '2010-05-27 14:24:40', 62, 0, '0000-00-00 00:00:00', '2010-04-30 10:41:03', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 1, '', '', 0, 9, 'robots=\nauthor=');

